     1→'use client';
     2→import { type FC, useCallback, useEffect, useRef, useState } from 'react';
     3→import { ReviewProposalInput } from './components/review-proposals-input';
     4→import { Button } from '@/components/ui/button';
     5→import { Editor } from '@/components/ui/editor';
     6→import { newBlock } from '@/lib/editor/index';
     7→import type { JSONContent } from '@tiptap/react';
     8→import { useParams } from 'next/navigation';
     9→import {
    10→  getReviewProposalById,
    11→  updateReviewProposal,
    12→} from '@/db/review-proposals';
    13→import type { Message } from 'ai';
    14→import { readStreamableValue, useActions } from 'ai/rsc';
    15→import { v4 as uuidv4 } from 'uuid';
    16→import type { AI } from './actions';
    17→import type { Options } from '@/types/actions';
    18→import { useHistory } from '../components/workspace-history-list/context';
    19→import { cn, softPathReplace } from '@/lib/utils';
    20→import GeneratingOutputLoader from './components/GeneratingOutputLoader';
    21→import { SESSION_TYPES } from '@/components/editor/ExportContent';
    22→import { ReviewProposalsSettings } from './components/review-proposals-settings';
    23→import logger from '@/lib/logger';
    24→import type { UserInstruction } from './types';
    25→import axios from 'axios';
    26→import { PsciZapIcon } from '@/components/icons/psci-icons';
    27→import { usePostHog } from 'posthog-js/react';
    28→import { Switch } from '@/components/ui/switch';
    29→import { Label } from '@/shadcn/components/ui/label';
    30→import useSWR from 'swr';
    31→import {
    32→  ResizableHandle,
    33→  ResizablePanel,
    34→  ResizablePanelGroup,
    35→} from '@/shadcn/components/ui/resizable';
    36→import { useWorkspaceDispatch, useWorkspaceState } from '../provider';
    37→import { exists } from '@/lib/type-guards';
    38→
    39→interface ReviewProposalProps {
    40→  threadId?: string;
    41→  history?: Message[];
    42→  persist?: boolean;
    43→}
    44→
    45→/**
    46→ * A component for reviewing proposals using AI.
    47→ *
    48→ * This component allows users to enter a requirement and a response, and then
    49→ * runs an AI review on the response. The AI review generates output based on the
    50→ * requirement and the response, which is then displayed in the component.
    51→ *
    52→ * The component also provides a button for running the AI review and a loading
    53→ * indicator while the AI review is running.
    54→ *
    55→ * @param {string} [requirement] - The requirement to evaluate the response against.
    56→ * @param {string} [response] - The response to evaluate.
    57→ * @param {boolean} [persist] - Whether to persist the AI output in the database.
    58→ *
    59→ * @returns {ReactElement} - The ReviewProposalUI component.
    60→ */
    61→export const ReviewProposalUI: FC<ReviewProposalProps> = () => {
    62→  const params = useParams();
    63→  const dispatch = useWorkspaceDispatch();
    64→  const workspaceState = useWorkspaceState();
    65→
    66→  const [requirement, setRequirement] = useState('');
    67→  const [response, setResponse] = useState('');
    68→  const [editor, setEditor] = useState<any>(null);
    69→  const [isGenerating, setIsGenerating] = useState(false);
    70→  const [isRequirementsToggleOn, setIsRequirementsToggleOn] =
    71→    useState<boolean>(false);
    72→  const { submitUserMessage } = useActions<typeof AI>();
    73→  const [isReviewProposalDataLoaded, setIsReviewProposalDataLoaded] =
    74→    useState<boolean>(false);
    75→  const [loading, setLoading] = useState(true);
    76→  const [reviewProposal, setReviewProposal] = useState<any>(null);
    77→  const toolbarRef = useRef<HTMLDivElement | null>(null);
    78→  const [toolbarHeight, setToolbarHeight] = useState<number>(40);
    79→  const { refreshData } = useHistory();
    80→  const posthog = usePostHog();
    81→
    82→  /**
    83→   * Initiates the smart check process for reviewing a proposal.
    84→   *
    85→   * This function creates a new user message with detailed instructions
    86→   * for evaluating a proposal response based on specific review criteria.
    87→   * It formats the instructions and proposal details, then submits the message
    88→   * for evaluation. The result of the evaluation is processed and displayed.
    89→   *
    90→   * Key steps include:
    91→   * - Setting the generating state to true.
    92→   * - Creating a new user message with formatted evaluation instructions and proposal details.
    93→   * - Submitting the message for processing.
    94→   * - Handling the response by updating the editor content and proposal state.
    95→   * - Refreshing data and resetting the generating state upon completion.
    96→   *
    97→   * It also manages the editor's content and track changes in the proposal review ID.
    98→   */
    99→  const handleRunSmartCheck = async () => {
   100→    setIsGenerating(true);
   101→    posthog.capture('run_ai_review_button_clicked', {
   102→      page: 'review-proposal',
   103→      settings: userInstructions,
   104→    });
   105→
   106→    const newUserMessage: Message = {
   107→      id: uuidv4(),
   108→      role: 'user',
   109→      createdAt: new Date(),
   110→      content: `
   111→            You are a proposal evaluator for government contracts. Your role is to critically analyze proposal responses based on specific Review Instructions provided.
   112→
   113→            **Instructions:**
   114→
   115→            - You will be given one or more Review Instructions, each containing a **Name** and a **Review Instruction**.
   116→            - Apply each Review Instruction to the proposal response thoroughly.
   117→            - For each Review Instruction, output your evaluation as per the instruction's requirements.
   118→
   119→            **Formatting Guidelines:**
   120→
   121→            - Begin each evaluation with the **Name** of the Review Instruction.
   122→            - Provide your evaluation under the Name, following any specific output guidelines in the Review Instruction.
   123→            - Separate each evaluation with two newline spacings for clarity.
   124→
   125→            **Example Format:**
   126→
   127→            Name: Compliance
   128→
   129→            Evaluation:
   130→            [Your evaluation...]
   131→
   132→            Name: Readability Level
   133→
   134→            Evaluation:
   135→            [Your evaluation...]
   136→
   137→            *Continue this pattern for all Review Instructions.*
   138→
   139→            **Remember:**
   140→
   141→            - Be precise, objective, and comprehensive in your evaluations.
   142→            - Provide actionable feedback and suggestions where appropriate.
   143→            - Ensure your language is professional and adheres to government contracting standards.
   144→
   145→            **Here are the Review Instructions:**
   146→
   147→            ${userInstructions?.map((ins: UserInstruction) => `Name: ${ins.proposal_reviews_ai_instruction.title}\n Review Instruction:\n${ins.proposal_reviews_ai_instruction.description}`).join('\n\n')}
   148→
   149→            **Here is the requirement and the proposal response:**
   150→
   151→            Requirement:
   152→            ${requirement}
   153→
   154→            Response:
   155→            ${response}
   156→        `,
   157→    };
   158→
   159→    const options: Partial<Options> = {
   160→      persist: true,
   161→      needsTitle: true,
   162→      // @ts-expect-error not sure fix
   163→      chatId: params.id ? params.id : null,
   164→    };
   165→
   166→    const result = await submitUserMessage(
   167→      newUserMessage,
   168→      options,
   169→      'review-proposal',
   170→      {
   171→        requirement,
   172→        response,
   173→      }
   174→    );
   175→
   176→    if (result) {
   177→      setRequirement('');
   178→      setResponse('');
   179→
   180→      const text = editor.getText();
   181→      const hasExistingContent = text.trim().length > 0;
   182→      const spaceToAdd = hasExistingContent ? '\n\n' : '';
   183→
   184→      let tempContent = `${spaceToAdd}Requirement: ${requirement}\n\n`;
   185→      tempContent += `Response: ${response}`;
   186→      setEditorContent(tempContent, true);
   187→      newBlock(editor);
   188→
   189→      for await (const v of readStreamableValue(result.stream)) {
   190→        if (exists(v) && 'content' in v) setEditorContent(v.content, true);
   191→
   192→        if ('chatId' in v) {
   193→          setReviewProposal(() => ({ ...reviewProposal, id: v.chatId }));
   194→          softPathReplace(`/review-proposal/${v.chatId}`);
   195→        }
   196→      }
   197→      await refreshData();
   198→      setIsGenerating(false);
   199→      // * Only do this on first run, reviewProposal is a placeholder object which only has id at this point, following is handled by onDebouncedUpdate.
   200→      if (!reviewProposal?.user_id) saveContent(editor);
   201→    }
   202→  };
   203→  const setEditorContent = useCallback(
   204→    (content: JSONContent | string, isInsert = false) => {
   205→      if (content) {
   206→        const cleanContent = content.replace(/-\s*$(?![\r\n])/gm, '');
   207→        let parsedContent = cleanContent;
   208→
   209→        // It might be JSON content, though I can't see where it ever will be.  Catch the exception to handle
   210→        // it not being JSON.
   211→        try {
   212→          parsedContent = JSON.parse(cleanContent);
   213→          // eslint-disable-next-line @typescript-eslint/no-unused-vars
   214→        } catch (error) {
   215→          // Expected error so squelch.
   216→        }
   217→
   218→        try {
   219→          if (isInsert) editor?.commands.insertContent(parsedContent);
   220→          else editor?.commands.setContent(parsedContent);
   221→        } catch (error) {
   222→          console.error(error);
   223→          if (isInsert)
   224→            try {
   225→              editor?.commands.insertContent(parsedContent);
   226→            } catch (error) {
   227→              console.error(error);
   228→            }
   229→          else editor?.commands.setContent(parsedContent);
   230→        }
   231→      }
   232→    },
   233→    [editor]
   234→  );
   235→
   236→  const fetchReviewProposal = useCallback(async () => {
   237→    const reviewProposal = await getReviewProposalById(params.id as string);
   238→
   239→    if (!reviewProposal) return;
   240→
   241→    setReviewProposal(reviewProposal);
   242→    editor?.commands.clearContent();
   243→    if (exists(reviewProposal.content))
   244→      setEditorContent(reviewProposal.content);
   245→  }, [editor?.commands, params.id, setEditorContent]);
   246→
   247→  useEffect(() => {
   248→    /**
   249→     * Fetches the review proposal data and sets the loading state to false.
   250→     *
   251→     * @async
   252→     * @function
   253→     * @returns {Promise<void>}
   254→     */
   255→    const fetchData = async () => {
   256→      await fetchReviewProposal();
   257→      setLoading(false);
   258→      setIsReviewProposalDataLoaded(true);
   259→    };
   260→
   261→    if (params.id && !editor) {
   262→      fetchData();
   263→    } else if (editor) {
   264→      setLoading(false);
   265→      setIsReviewProposalDataLoaded(true);
   266→    }
   267→    // eslint-disable-next-line react-hooks/exhaustive-deps
   268→  }, [editor, params.id]);
   269→
   270→  /**
   271→   * Save the current editor content to the review proposal
   272→   *
   273→   * @param editor - The current editor instance
   274→   */
   275→  const saveContent = useCallback(
   276→    async (editor: any) => {
   277→      if (reviewProposal?.id && !loading && !isGenerating) {
   278→        const updatedReviewProposal = await updateReviewProposal(
   279→          reviewProposal.id,
   280→          {
   281→            content: editor.getJSON() || '',
   282→            updated_at: new Date().toISOString(),
   283→          }
   284→        );
   285→        setReviewProposal(updatedReviewProposal);
   286→      }
   287→    },
   288→    [reviewProposal?.id, loading, isGenerating]
   289→  );
   290→
   291→  const fetcher = async (url: string) => {
   292→    try {
   293→      const res = await axios.get(url);
   294→      return res?.data?.data || [];
   295→    } catch (cause) {
   296→      throw new Error('Failed to fetch the user instructions:', { cause });
   297→    }
   298→  };
   299→
   300→  const {
   301→    data: userInstructions,
   302→    mutate: mutateUserInstructions,
   303→    isLoading: isFetchingInstructions,
   304→    error,
   305→  } = useSWR<UserInstruction[]>('/api/ai-review/instructions', fetcher);
   306→
   307→  if (error) logger.error('Failed to fetch the user instructions:', error);
   308→
   309→  const isSubmitDisabled =
   310→    !response ||
   311→    isGenerating ||
   312→    (isRequirementsToggleOn && !requirement) ||
   313→    !userInstructions?.length;
   314→
   315→  // Set the height of the toolbar dynamically to calculate the height of the editor
   316→  const updateToolbarHeight = useCallback(() => {
   317→    if (toolbarRef.current) setToolbarHeight(toolbarRef.current.clientHeight);
   318→  }, []);
   319→
   320→  useEffect(() => {
   321→    window.addEventListener('resize', updateToolbarHeight);
   322→    return () => {
   323→      window.removeEventListener('resize', updateToolbarHeight);
   324→    };
   325→  }, [updateToolbarHeight]);
   326→
   327→  useEffect(() => {
   328→    updateToolbarHeight();
   329→  }, [reviewProposal, updateToolbarHeight]);
   330→
   331→  // Effect to handle ADD_TO_DRAFT updates
   332→  useEffect(() => {
   333→    if (workspaceState.contents && editor && isReviewProposalDataLoaded) {
   334→      const text = editor.getText();
   335→      const hasExistingContent = text.trim().length > 0;
   336→      const spaceToAdd = hasExistingContent ? '\n\n' : '';
   337→
   338→      setEditorContent(spaceToAdd + workspaceState.contents, true);
   339→      saveContent(editor);
   340→      dispatch({ type: 'SET_CONTENTS', payload: '' });
   341→    }
   342→  }, [
   343→    workspaceState.contents,
   344→    isReviewProposalDataLoaded,
   345→    editor,
   346→    saveContent,
   347→    setEditorContent,
   348→    dispatch,
   349→  ]);
   350→
   351→  useEffect(() => {
   352→    if (isReviewProposalDataLoaded)
   353→      if (reviewProposal && reviewProposal.content !== null)
   354→        setEditorContent(reviewProposal.content);
   355→  }, [isReviewProposalDataLoaded, reviewProposal, setEditorContent]);
   356→
   357→  useEffect(
   358→    () => () => {
   359→      dispatch({ type: 'SET_CONTENTS', payload: '' });
   360→    },
   361→    [dispatch]
   362→  );
   363→
   364→  return (
   365→    <div className='relative flex flex-col px-5 pt-3 pb-1 max-h-[calc(100vh_-_164px)]'>
   366→      <div className='flex w-full flex-col items-center justify-between md:flex-row mb-5'>
   367→        <div className='flex w-full flex-col'>
   368→          <h2 className='text-2xl font-semibold'>AI Proposal Reviewer</h2>
