    39→  PsciTrashIcon,
    40→  PsciUploadIcon,
    41→  PsciMessageCircle,
    42→  PsciClipboardCheck,
    43→  PsciFileText,
    44→  PsciTag,
    45→  PsciInfoIcon,
    46→  PsciFolderPlusIcon,
    47→} from '@/components/icons/psci-icons';
    48→import {
    49→  exportContent,
    50→  FILE_TYPES,
    51→  SESSION_TYPES,
    52→} from '@/components/editor/ExportContent';
    53→import { usePostHog } from 'posthog-js/react';
    54→import { SessionTagEditorDialog } from './session-tag-editor-dialog';
    55→import { Badge } from '@/components/ui/badge';
    56→import { useHistory } from './context';
    57→import {
    58→  Tooltip,
