    61→} from '@/components/ui/tooltip';
    62→
    63→export type SessionItemTag = {
    64→  session_tag_id: string;
    65→  name: string;
    66→  color: string;
    67→};
    68→import { useHistoryPanel } from '@/context/historyPanelContext/history-panel-context';
    69→import { MoveSessionsToFolderDialog } from './move-sessions-to-folder-dialog';
    70→
    71→export type HistoryItemType = {
    72→  id: string;
    73→  type: SessionType;
    74→  created_at: string;
    75→  updated_at: string | null;
    76→  folder_id?: string | null;
    77→  item_group_id?: string | null;
    78→  name: string | null;
    79→  tags: SessionItemTag[] | null;
    80→};
    81→
    82→export type FilterPayload = {
    83→  query: string;
    84→  sessionType?: SessionType;
    85→  rangeDate?: HistoryRangeDateState;
    86→  selectedSessionTags?: SessionItemTag[];
    87→};
    88→
    89→export type HistoryContextType = {
    90→  itemsWithItemGroup: HistoryItemType[] | undefined;
    91→  sortedData: Record<string, HistoryItemType[]>;
    92→  itemGroups: ItemGroup[] | undefined;
    93→  deleteItem: (type: string, id: string, isActive: boolean) => Promise<void>;
    94→  deleteItems: (
    95→    historyItems: HistoryItemType[],
    96→    activeItemId: string
    97→  ) => Promise<string[]>;
    98→  moveSessionsToItemGroup: (
    99→    sessions: HistoryItemType[],
   100→    groupItemId: string
   101→  ) => Promise<string[]>;
   102→  updateItem: (type: string, id: string, name: string) => Promise<void>;
   103→  setFilters: React.Dispatch<React.SetStateAction<FilterPayload>>;
   104→  filters: FilterPayload;
   105→  isLoading: boolean;
   106→  isInitialLoading: boolean;
   107→  refreshData: () => Promise<void>;
   108→  createItemGroup: (name?: string) => Promise<void>;
   109→  updateItemGroup: (
   110→    itemId: string,
   111→    groupItemId: string | null
   112→  ) => Promise<void>;
   113→  updateItemGroupName: (groupItem: ItemGroup, name: string) => Promise<void>;
   114→  deleteItemGroup: (id: string) => Promise<void>;
   115→  selectedItem: HistoryItemType | null;
