   117→};
   118→
   119→interface HistoryItemProps {
   120→  type: SessionType;
   121→  item: HistoryItemType;
   122→  onDelete?: (type: string, id: string, isActive: boolean) => void;
   123→  onUpdate?: (type: string, id: string, name: string) => void;
   124→  refetchTags: () => Promise<void>;
   125→}
   126→
   127→export const chatTypeToIcon = (
   128→  type: SessionType,
   129→  isActiveItem: boolean,
   130→  isHistoryPanelCollapsed = false,
   131→  size = 16
   132→) => {
   133→  const iconStyle = cn(
   134→    'inline',
   135→    isActiveItem && isHistoryPanelCollapsed && 'text-primary'
   136→  );
   137→  switch (type) {
   138→    case 'chat':
   139→      return (
   140→        <PsciMessageCircle
   141→          size={size}
   142→          className={cn(iconStyle, 'text-green-500')}
   143→        />
   144→      );
   145→    case 'reviewProposal':
   146→      return (
   147→        <PsciClipboardCheck
   148→          size={size}
   149→          className={cn(iconStyle, 'text-purple-500')}
   150→        />
   151→      );
   152→    case 'draft':
   153→      return (
   154→        <PsciFileText size={size} className={cn(iconStyle, 'text-pink-500')} />
   155→      );
   156→    default:
   157→      return null;
   158→  }
   159→};
   160→export const HistoryItem = forwardRef<HTMLAnchorElement, HistoryItemProps>(
   161→  ({ type, item, onDelete, onUpdate, refetchTags }, ref) => {
   162→    const { refreshData } = useHistory();
   163→    const [openEditDialog, setOpenEditDialog] = useState(false);
   164→    const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
   165→    const [itemName, setItemName] = useState(item.name ?? '');
   166→    const [
   167→      isOpenAttachToOpportunityDialog,
   168→      setIsOpenAttachToOpportunityDialog,
   169→    ] = useState<boolean>(false);
   170→    const [isOpenSessionTagEditorDialog, setIsOpenSessionTagEditorDialog] =
   171→      useState<boolean>(false);
   172→    const [isOpenMoveToFolderDialog, setIsOpenMoveToFolderDialog] =
   173→      useState(false);
   174→    const pathname = usePathname();
   175→    const pathSegments = pathname.split('/');
   176→    const itemId = pathSegments[2];
   177→    const posthog = usePostHog();
   178→    const { setSelectedItem } = useHistory();
   179→    const tagsDivRef = useRef<HTMLDivElement>(null);
   180→    const [maxVisibleTags, setMaxVisibleTags] = useState<number>(0);
   181→    const [calculatingVisibleTags, setCalculatingVisibleTags] = useState(true);
   182→    const showTagsToolTipBadge =
   183→      !calculatingVisibleTags && item.tags && item.tags.length > maxVisibleTags;
   184→
   185→    const isActiveItem = item.id === itemId;
   186→    const link =
   187→      type === 'reviewProposal'
   188→        ? `/review-proposal/${item.id}`
   189→        : `/${type}/${item.id}`;
   190→
   191→    const onInputNameChange = useCallback(
   192→      (e: React.ChangeEvent<HTMLInputElement>) => {
   193→        const value = e.target.value.trimStart();
   194→        setItemName(value);
   195→      },
   196→      []
   197→    );
   198→
   199→    const handleDeleteConfirmation = useCallback(() => {
   200→      onDelete?.(type, item.id, isActiveItem);
   201→      setOpenDeleteDialog(false);
   202→    }, [type, item.id, isActiveItem, onDelete]);
   203→
   204→    const handleUpdateConfirmation = useCallback(() => {
   205→      onUpdate?.(type, item.id, itemName);
   206→      setOpenEditDialog(false);
   207→    }, [type, item.id, itemName, onUpdate]);
   208→
   209→    const handleOnEditIconClick: MouseEventHandler<HTMLDivElement> =
   210→      useCallback(e => {
   211→        e.stopPropagation();
   212→        setOpenEditDialog(true);
   213→      }, []);
   214→
   215→    const handleEditCancel: MouseEventHandler<HTMLDivElement> =
   216→      useCallback(() => {
   217→        setOpenEditDialog(false);
   218→        setItemName(item.name ?? '');
   219→      }, [item.name]);
   220→
   221→    const handleOnDeleteIconClick: MouseEventHandler<HTMLDivElement> =
   222→      useCallback(e => {
   223→        e.stopPropagation();
   224→        setOpenDeleteDialog(true);
   225→      }, []);
   226→
   227→    const handleAttachToOpportunityClick: MouseEventHandler<HTMLDivElement> =
   228→      useCallback(
   229→        e => {
   230→          e.stopPropagation();
   231→          setIsOpenAttachToOpportunityDialog(true);
   232→          posthog.capture('attach_to_opportunity_button_clicked');
   233→        },
   234→        [posthog]
   235→      );
   236→
   237→    const handleMoveToProjectClick: MouseEventHandler<HTMLDivElement> =
   238→      useCallback(
   239→        e => {
   240→          e.stopPropagation();
   241→          setIsOpenMoveToFolderDialog(true);
   242→        },
   243→        [setIsOpenMoveToFolderDialog]
   244→      );
   245→
   246→    const handleSessionEditTagsClick: MouseEventHandler<HTMLDivElement> =
   247→      useCallback(
   248→        e => {
   249→          e.stopPropagation();
   250→          setIsOpenSessionTagEditorDialog(true);
   251→          posthog.capture('session_edit_tags_button_clicked');
   252→        },
   253→        [posthog]
   254→      );
   255→
   256→    const handleExport = useCallback(
   257→      async (
   258→        e: MouseEvent<HTMLDivElement>,
   259→        type: FILE_TYPES,
   260→        sessionType: SESSION_TYPES
   261→      ) => {
   262→        e.stopPropagation();
   263→        await exportContent(type, item.id, item.name ?? '', sessionType);
   264→      },
   265→      [item.id, item.name]
   266→    );
   267→    const { isHistoryPanelCollapsed, toggleHistoryPanel } = useHistoryPanel();
   268→
   269→    const visibleTags = item.tags
   270→      ?.slice(0, maxVisibleTags)
   271→      .map((tag: SessionItemTag) => (
   272→        <Badge
   273→          className='max-w-[160px] truncate inline-block text-foreground'
   274→          key={tag.name}
   275→          style={{ backgroundColor: tag.color }}
   276→        >
   277→          {tag.name}
   278→        </Badge>
   279→      ));
   280→
   281→    const tooltipBadgeTags = item.tags?.map((tag: SessionItemTag) => (
   282→      <Badge key={tag.name} style={{ backgroundColor: tag.color }}>
   283→        {tag.name}
   284→      </Badge>
   285→    ));
   286→
   287→    useEffect(() => {
   288→      if (!tagsDivRef.current || isHistoryPanelCollapsed) return;
   289→
   290→      const calculateVisibleTags = (entry: ResizeObserverEntry) => {
   291→        setCalculatingVisibleTags(true);
   292→        const availableTagsDivWidth = entry.contentRect.width;
   293→        const maxTagWidth = 160;
   294→        let tagsThatCanBeShown = 0;
   295→        let tagsDivWidth = 0;
   296→
   297→        if (item.tags)
   298→          for (const tag of item.tags) {
   299→            // Assuming each character is 10px wide
   300→            // If a tag has only one character, shadcn badge uses approx 30px to display it
   301→            let tagWidth = 20 + tag.name.length * 10;
   302→
   303→            if (tagWidth > maxTagWidth) tagWidth = 160;
   304→
   305→            // 4px is gap between tags
   306→            tagWidth += 4;
   307→
   308→            // 40px for tooltip badge
   309→            if (tagsDivWidth + tagWidth + 40 < availableTagsDivWidth) {
   310→              tagsDivWidth += tagWidth;
   311→              tagsThatCanBeShown += 1;
   312→            } else {
   313→              break;
   314→            }
   315→          }
   316→
   317→        setMaxVisibleTags(tagsThatCanBeShown);
   318→        setCalculatingVisibleTags(false);
   319→      };
   320→
   321→      const debouncedCalculateVisibleTags = debounce(calculateVisibleTags, 200);
   322→
   323→      const observer = new ResizeObserver(entries => {
   324→        // Since we are only observing one element, we can safely use entries[0]
   325→        debouncedCalculateVisibleTags(entries[0]);
   326→      });
   327→
   328→      observer.observe(tagsDivRef.current);
   329→
   330→      return () => {
   331→        debouncedCalculateVisibleTags.cancel();
   332→        observer.disconnect();
   333→      };
   334→    }, [isHistoryPanelCollapsed, item.tags]);
   335→
   336→    return (
   337→      <>
   338→        {!isHistoryPanelCollapsed && (
   339→          <Link
   340→            ref={ref}
   341→            href={link}
   342→            className={cn(
   343→              'text-sm justify-between text-muted-foreground overflow-hidden truncate whitespace-nowrap rounded group my-1 px-2 py-1 mx-2 flex flex-wrap items-center hover:bg-muted hover:text-foreground cursor-pointer transition-all',
   344→              isActiveItem && 'bg-muted'
   345→            )}
   346→            onClick={() => setSelectedItem(item)}
   347→          >
   348→            <div className={cn('truncate max-w-[90%] flex')}>
   349→              <div className='mr-1.5 -mt-[2px]'>
   350→                {chatTypeToIcon(type, isActiveItem, isHistoryPanelCollapsed)}
   351→              </div>
   352→              <span title={item.name ?? ''} className='truncate'>
   353→                {(item.name ?? '').slice(0, 40)}
   354→              </span>
   355→            </div>
   356→            <DropdownMenu>
   357→              <DropdownMenuTrigger
   358→                className='-mr-1'
   359→                onClick={e => e.stopPropagation()}
   360→              >
   361→                <PsciEllipsisVertical
   362→                  size={16}
   363→                  data-testid={`more-options-${toKebabCase(item?.name ?? '')}`}
   364→                />
   365→              </DropdownMenuTrigger>
   366→              <DropdownMenuContent>
   367→                <DropdownMenuItem
   368→                  className='flex gap-2 text-xs py-2 font-[inherit]'
   369→                  onClick={handleOnEditIconClick}
   370→                  data-testid={`rename-${toKebabCase(item?.name ?? '')}`}
   371→                >
   372→                  <PsciEditIcon className='size-4' />
   373→                  Rename
   374→                </DropdownMenuItem>
   375→                <DropdownMenuItem
   376→                  className='flex gap-2 text-xs py-2'
   377→                  onClick={handleSessionEditTagsClick}
   378→                  data-testid={`session_edit_tags-${toKebabCase(item?.name ?? '')}`}
   379→                >
   380→                  <PsciTag className='size-4' />
   381→                  Edit Tags
   382→                </DropdownMenuItem>
   383→                <DropdownMenuItem
   384→                  className='flex gap-2 text-xs py-2'
   385→                  onClick={handleAttachToOpportunityClick}
   386→                  data-testid={`attach-to-opportunity-${toKebabCase(item?.name ?? '')}`}
   387→                >
   388→                  <PsciPaperclipIcon className='size-4' />
   389→                  Attach to Opportunity
   390→                </DropdownMenuItem>
   391→                <DropdownMenuItem
   392→                  className='flex gap-2 text-xs py-2'
   393→                  onClick={handleMoveToProjectClick}
   394→                  data-testid={`move-to-project-${toKebabCase(item?.name ?? '')}`}
   395→                >
   396→                  <PsciFolderPlusIcon className='size-4' />
   397→                  Move to Project
   398→                </DropdownMenuItem>
   399→                {(type === SESSION_TYPES.REVIEW_PROPOSAL ||
   400→                  type === SESSION_TYPES.DRAFT) && (
   401→                  <DropdownMenuSub>
   402→                    <DropdownMenuSubTrigger
   403→                      data-testid={`export-button-${toKebabCase(item?.name ?? '')}`}
   404→                      className='flex gap-2 text-xs py-2'
   405→                    >
   406→                      <PsciUploadIcon className='size-4' /> Export
   407→                    </DropdownMenuSubTrigger>
   408→                    <DropdownMenuSubContent>
   409→                      <DropdownMenuItem
   410→                        className='flex gap-2 text-xs py-2'
   411→                        onClick={e =>
   412→                          handleExport(e, FILE_TYPES.PDF, type as SESSION_TYPES)
   413→                        }
   414→                        data-testid={`export-to-pdf-${toKebabCase(item?.name ?? '')}`}
   415→                      >
   416→                        <PsciUploadIcon className='size-4' /> PDF
   417→                      </DropdownMenuItem>
   418→                      <DropdownMenuItem
   419→                        className='flex gap-2 text-xs py-2'
   420→                        onClick={e =>
   421→                          handleExport(
   422→                            e,
   423→                            FILE_TYPES.DOCX,
   424→                            type as SESSION_TYPES
   425→                          )
   426→                        }
   427→                        data-testid={`export-to-docx-${toKebabCase(item?.name ?? '')}`}
   428→                      >
   429→                        <PsciUploadIcon className='size-4' /> DOCX
   430→                      </DropdownMenuItem>
   431→                    </DropdownMenuSubContent>
   432→                  </DropdownMenuSub>
   433→                )}
   434→                <DropdownMenuItem
   435→                  className='flex gap-2 text-xs py-2'
   436→                  onClick={handleOnDeleteIconClick}
   437→                  data-testid={`delete-${toKebabCase(item?.name ?? '')}`}
   438→                >
   439→                  <PsciTrashIcon className='size-4' /> Delete
   440→                </DropdownMenuItem>
   441→              </DropdownMenuContent>
   442→            </DropdownMenu>
   443→            {item?.tags && item.tags.length > 0 && (
   444→              <div
   445→                className={cn(
   446→                  'flex items-center gap-1 w-full flex-wrap pb-1',
   447→                  (item.tags?.length ?? 0) > 0 && 'pt-2'
   448→                )}
   449→                ref={tagsDivRef}
   450→              >
   451→                {!calculatingVisibleTags && visibleTags}
   452→                {showTagsToolTipBadge && (
   453→                  <Tooltip>
   454→                    <TooltipTrigger>
   455→                      <Badge className='cursor-pointer'>
   456→                        <PsciInfoIcon size={16}></PsciInfoIcon>
   457→                      </Badge>
   458→                    </TooltipTrigger>
   459→                    <TooltipContent>
   460→                      <div className='p-2 flex items-center gap-1 w-full flex-wrap'>
   461→                        {tooltipBadgeTags}
   462→                      </div>
   463→                    </TooltipContent>
   464→                  </Tooltip>
   465→                )}
   466→              </div>
   467→            )}
   468→          </Link>
   469→        )}
   470→
   471→        {isHistoryPanelCollapsed && (
   472→          <div
   473→            className='flex items-center justify-center text-center my-3'
   474→            onClick={() => toggleHistoryPanel(false)}
   475→          >
   476→            {chatTypeToIcon(type, isActiveItem, isHistoryPanelCollapsed, 22)}
   477→          </div>
   478→        )}
   479→
   480→        <CommonDialogBox
   481→          open={openDeleteDialog}
   482→          setOpen={setOpenDeleteDialog}
   483→          title='Confirm Deletion'
   484→          content='Are you sure you want to delete this item?'
   485→          onContinue={handleDeleteConfirmation}
   486→          continueButtonText='Delete'
   487→          onCancel={() => setOpenDeleteDialog(false)}
   488→          confirmButtonClass='bg-destructive/90 text-destructive-foreground hover:bg-destructive'
   489→        />
   490→
   491→        <CommonDialogBox
   492→          open={openEditDialog}
   493→          setOpen={setOpenEditDialog}
   494→          title={`Update ${capitalizeAndJoin(item.type)}`}
   495→          content={
   496→            <Input
   497→              value={itemName}
   498→              onChange={onInputNameChange}
   499→              className='max-w-[75%] mx-auto'
   500→            />
   501→          }
   502→          onContinue={handleUpdateConfirmation}
   503→          continueButtonText='Update'
   504→          onCancel={handleEditCancel}
   505→          confirmButtonClass='bg-primary/90 text-primary-foreground hover:bg-primary'
   506→          disableContinueButton={!itemName}
   507→        />
   508→
   509→        {isOpenAttachToOpportunityDialog && (
   510→          <AttachToOpportunityDialog
   511→            isOpen={isOpenAttachToOpportunityDialog}
   512→            setIsOpen={setIsOpenAttachToOpportunityDialog}
   513→            items={[item]}
   514→          />
   515→        )}
   516→
   517→        {isOpenSessionTagEditorDialog && (
   518→          <SessionTagEditorDialog
   519→            open={isOpenSessionTagEditorDialog}
   520→            setIsOpen={setIsOpenSessionTagEditorDialog}
   521→            selectedSession={item}
   522→            refetchHistoryPanelData={refreshData}
   523→            refetchTagsToUpdateFilter={refetchTags}
   524→          />
   525→        )}
   526→
   527→        {isOpenMoveToFolderDialog && (
   528→          <MoveSessionsToFolderDialog
   529→            isOpen={isOpenMoveToFolderDialog}
   530→            setIsOpen={setIsOpenMoveToFolderDialog}
   531→            items={[item]}
   532→          />
