import { toast } from 'sonner';

export enum FILE_TYPES {
  DOCX = 'docx',
  PDF = 'pdf',
}

export enum SESSION_TYPES {
  DRAFT = 'draft',
  CHAT = 'chat',
  REVIEW_PROPOSAL = 'reviewProposal',
}

/**
 * Exports content based on the specified file type and session type.
 *
 * Initiates a request to generate and download a file of the given type
 * for the specified session. Displays a toast notification indicating
 * the status of the operation.
 *
 * @param fileType - The type of file to generate (e.g., DOCX, PDF).
 * @param id - The unique identifier for the content to be exported.
 * @param sessionType - The type of session (e.g., DRAFT, CHAT, REVIEW_PROPOSAL).
 *
 * @throws Will throw an error if the file generation fails.
 */
export const exportContent = async (
  fileType: FILE_TYPES,
  id: string,
  fileName: string,
  sessionType: SESSION_TYPES
) => {
  toast.promise(
    (async () => {
      const response = await fetch(
        `/api/export/${id}/generate-${fileType}?session_type=${sessionType}`,
        {
          method: 'GET',
        }
      );
      if (response.ok) {
        const blob = await response.blob();
        const downloadUrl = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = downloadUrl;
        link.download = `${fileName || id}.${fileType}`;
        document.body.appendChild(link);
        link.click();
        link.remove();
        URL.revokeObjectURL(downloadUrl);
        return null;
      }

      throw new Error(`Failed to generate ${fileType.toUpperCase()}`);
    })(),
    {
      loading: `Generating ${fileType.toUpperCase()}...`,
      success: `${fileType.toUpperCase()} generated successfully!`,
      error: `Error generating ${fileType.toUpperCase()}`,
      closeButton: false,
    }
  );
};

