import logger from '@/lib/logger';
import { createClient } from '@/lib/supabase/server';

/**
 * Fetches the content of a draft by its ID.
 *
 * @param draftId The ID of the draft to fetch.
 * @returns The content of the draft as a JSON string, or null if the draft is not found.
 * @throws An error if the request fails unexpectedly.
 */
export async function getDraftContentById(draftId: string) {
  try {
    const supabase = await createClient();
    const { data } = await supabase
      .from('drafts')
      .select('contents')
      .eq('id', draftId)
      .maybeSingle()
      .throwOnError();

    return data?.contents ?? null;
  } catch (error) {
    logger.error('Failed to fetch the draft content.', error);
    return null;
  }
}

/**
 * Fetches the content of a review proposal by its ID.
 * @param reviewProposalId - The ID of the review proposal to fetch.
 * @returns A Promise that resolves to the content of the review proposal,
 *          or null if an error occurs or the review proposal is not found.
 */
export async function getReviewProposalContentById(reviewProposalId: string) {
  try {
    const supabase = await createClient();
    const { data } = await supabase
      .from('review_proposals')
      .select('content')
      .eq('id', reviewProposalId)
      .maybeSingle()
      .throwOnError();

    return data?.content ?? null;
  } catch (error) {
    logger.error('Failed to fetch the review proposal content.', error);
    return null;
  }
}

'use client';

import {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
} from '@/components/ui/dropdown-menu';
import { Button } from '@/components/ui/button';
import {
  PsciFolderPlusIcon,
  PsciChevronDown,
  PsciPanelRightClose,
  PsciBriefcase,
} from '@/components/icons/psci-icons';
import {
  type MouseEvent,
  type MouseEventHandler,
  useCallback,
  useState,
} from 'react';
import {
  exportContent,
  FILE_TYPES,
  SESSION_TYPES,
} from '@/components/editor/ExportContent';
import { useHistory } from '../workspace-history-list/context';
import { usePostHog } from 'posthog-js/react';
import { AttachToOpportunityDialog } from '../workspace-history-list/attach-to-opportunity-dialog';
import { MoveSessionsToFolderDialog } from '../workspace-history-list/move-sessions-to-folder-dialog';
import { FilePdf, FileDoc } from '@phosphor-icons/react';
import { NewSubMenu } from './NewSubMenu';
import { SaveAsSubMenu } from './SaveAsSubMenu';
import { OpenSubMenu } from './OpenSubMenu';
import { useHistoryPanel } from '@/context/historyPanelContext/history-panel-context';
import { cn } from '@/lib/utils';

export const AIHeader = () => {
  const { selectedItem } = useHistory();
  const { isHistoryPanelCollapsed, toggleHistoryPanel } = useHistoryPanel();
  const posthog = usePostHog();
  const [activeMenu, setActiveMenu] = useState<string | null>(null);
  const handleOpen = (menu: string) => setActiveMenu(menu);
  const handleClose = () => setActiveMenu(null);
  const [isOpenAttachToOpportunityDialog, setIsOpenAttachToOpportunityDialog] =
    useState<boolean>(false);
  const [isOpenMoveToFolderDialog, setIsOpenMoveToFolderDialog] =
    useState(false);

  const buttonClass = (menu: string) =>
    cn(
      'text-sm font-medium flex items-center gap-1 hover:bg-background/50',
      activeMenu === menu ? 'text-primary bg-background/50' : ''
    );

  const chevronClass = (menu: string) =>
    cn(
      'w-4 h-4 opacity-70 transition-transform',
      activeMenu === menu ? 'rotate-180' : ''
    );

  const isExportable =
    selectedItem?.type === SESSION_TYPES.DRAFT ||
    selectedItem?.type === SESSION_TYPES.REVIEW_PROPOSAL;

  const handleExport = useCallback(
    async (
      e: MouseEvent<HTMLDivElement>,
      type: FILE_TYPES,
      sessionType: SESSION_TYPES
    ) => {
      e.stopPropagation();
      await exportContent(
        type,
        selectedItem?.id ?? '',
        selectedItem?.name ?? '',
        sessionType
      );
    },
    [selectedItem?.id, selectedItem?.name]
  );

  const handleAttachToOpportunityClick: MouseEventHandler<HTMLDivElement> =
    useCallback(
      e => {
        e.stopPropagation();
        setIsOpenAttachToOpportunityDialog(true);
        posthog.capture('attach_to_opportunity_button_clicked_in_ai_header');
      },
      [posthog]
    );

  const handleMoveToFolderClick: MouseEventHandler<HTMLDivElement> =
    useCallback(
      e => {
        e.stopPropagation();
        setIsOpenMoveToFolderDialog(true);
        posthog.capture('move_to_folder_button_clicked_in_ai_header');
      },
      [posthog]
    );

  return (
    <div className='flex w-full items-center gap-2 border-b border-border bg-accent/50 px-4 py-1 shadow-sm'>
      <div className='flex items-center gap-2'>
        <DropdownMenu
          onOpenChange={open => (open ? handleOpen('sessions') : handleClose())}
        >
          <DropdownMenuTrigger asChild>
            <Button variant='ghost' className={buttonClass('sessions')}>
              Sessions
              <PsciChevronDown className={chevronClass('sessions')} />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent>
            <NewSubMenu />
            <DropdownMenuSeparator />
            <SaveAsSubMenu />
            <OpenSubMenu />
          </DropdownMenuContent>
        </DropdownMenu>

        <DropdownMenu
          onOpenChange={open => (open ? handleOpen('view') : handleClose())}
        >
          <DropdownMenuTrigger asChild>
            <Button variant='ghost' className={buttonClass('view')}>
              View
              <PsciChevronDown className={chevronClass('view')} />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent>
            <DropdownMenuItem
              className='cursor-pointer'
              onClick={() => toggleHistoryPanel(!isHistoryPanelCollapsed)}
            >
              <PsciPanelRightClose className='mr-2 h-4 w-4' /> Show/Hide History
              Panel
            </DropdownMenuItem>
            <DropdownMenuItem className='cursor-pointer' disabled={true}>
              <PsciPanelRightClose className='mr-2 h-4 w-4' /> Show/Hide Source
              Panel
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>

        <DropdownMenu
          onOpenChange={open => (open ? handleOpen('share') : handleClose())}
        >
          <DropdownMenuTrigger asChild>
            <Button variant='ghost' className={buttonClass('share')}>
              Share
              <PsciChevronDown className={chevronClass('share')} />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent>
            <DropdownMenuItem
              className='cursor-pointer'
              disabled={!selectedItem || Boolean(selectedItem.item_group_id)}
              onClick={handleMoveToFolderClick}
            >
              <PsciFolderPlusIcon className='mr-2 h-4 w-4' /> Add to Folder
            </DropdownMenuItem>
            <DropdownMenuItem
              className='cursor-pointer'
              disabled={!selectedItem}
              onClick={handleAttachToOpportunityClick}
            >
              <PsciBriefcase className='mr-2 h-4 w-4' /> Add to Opportunity
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>

        <DropdownMenu
          onOpenChange={open => (open ? handleOpen('export') : handleClose())}
        >
          <DropdownMenuTrigger asChild>
            <Button variant='ghost' className={buttonClass('export')}>
              Export
              <PsciChevronDown className={chevronClass('export')} />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent>
            <DropdownMenuItem
              className='cursor-pointer'
              disabled={!isExportable}
              onClick={e =>
                handleExport(
                  e,
                  FILE_TYPES.PDF,
                  selectedItem?.type as SESSION_TYPES.DRAFT
                )
              }
            >
              <FilePdf size={38} className='mr-2 h-4 w-4' /> Export as PDF
            </DropdownMenuItem>
            <DropdownMenuItem
              className='cursor-pointer'
              disabled={!isExportable}
              onClick={e =>
                handleExport(
                  e,
                  FILE_TYPES.DOCX,
                  selectedItem?.type as SESSION_TYPES.DRAFT
                )
              }
            >
              <FileDoc size={38} className='mr-2 h-4 w-4' /> Export as Word
              Document
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>

      {isOpenAttachToOpportunityDialog && (
        <AttachToOpportunityDialog
          isOpen={isOpenAttachToOpportunityDialog}
          setIsOpen={setIsOpenAttachToOpportunityDialog}
          items={selectedItem ? [selectedItem] : []}
        />
      )}

      {isOpenMoveToFolderDialog && (
        <MoveSessionsToFolderDialog
          isOpen={isOpenMoveToFolderDialog}
          setIsOpen={setIsOpenMoveToFolderDialog}
          items={selectedItem ? [selectedItem] : []}
          pruneCheckedItems={() => {}}
        />
      )}
    </div>
  );
};

