    32→
    33→/**
    34→ * Enhanced PDF Service for converting markdown content to PDF documents
    35→ *
    36→ * This service provides a comprehensive solution for generating PDFs from markdown content
    37→ * with the following features:
    38→ * - Clean, organized architecture with separated concerns
    39→ * - Optimized font management to reduce redundant font changes
    40→ * - Comprehensive markdown parsing (headings, links, bold text, lists, code blocks)
    41→ * - Responsive layout with proper page breaks and spacing
    42→ * - Configurable styling through centralized constants
    43→ * - Support for multiple pages with headers, footers, and page indicators
    44→ *
    45→ * @example
    46→ * ```typescript
    47→ * const pdfBlob = pdfService.markdownToPdf(['# Hello World\nThis is **bold** text']);
    48→ * ```
    49→ */
    50→@Injectable()
    51→export class PdfService {
    52→  readonly #logger = new Logger(PdfService.name);
    53→
    54→  private static readonly PDF_CONFIG = {
    55→    MARGINS: { top: 35, bottom: 35, left: 15, right: 15 },
    56→    HEADER: { height: 25, fontSize: 12, yPosition: 15 },
    57→    FOOTER: { height: 25, fontSize: 8 },
    58→    TEXT: { baseSize: 10, lineHeight: 5, headingLineHeight: 2.5 },
    59→    COLORS: {
    60→      header: [52, 73, 94] as const,
    61→      text: [44, 62, 80] as const,
    62→      link: [41, 128, 185] as const,
    63→      code: [85, 85, 85] as const,
    64→      border: [230, 230, 230] as const,
    65→      background: [248, 249, 250] as const,
    66→    },
    67→    FONTS: {
    68→      body: 'helvetica',
    69→      code: 'courier',
    70→    },
    71→  };
    72→
    73→  private static readonly MARKDOWN_PATTERNS = {
    74→    bold: /(\*\*|__)([^*_\n]*?)\1/g,
    75→    link: /\[([^\]]+)\]\(([^)]+)\)/g,
    76→    heading1: /^# (.+)$/,
    77→    heading2: /^## (.+)$/,
    78→    heading3: /^### (.+)$/,
    79→    bullet: /^(\s*)[-*] (.+)$/,
    80→    numbered: /^\d+\.\s(.+)$/,
    81→    code: /^(\s{4}|\t|> )(.*)$/,
    82→  };
    83→
    84→  // Global regex patterns for text segment parsing (compiled once for performance)
    85→  private static readonly GLOBAL_PATTERNS = {
    86→    bold: /(\*\*|__)([^*_\n]*?)\1/g,
    87→    link: /\[([^\]]+)\]\(([^)]+)\)/g,
    88→    trailingBold: /([^*\n]+)\*\*(?!\*)/g,
    89→  };
    90→
    91→  // Additional regex patterns for text parsing (compiled once for performance)
    92→  private static readonly PARSING_PATTERNS = {
    93→    indentation: /^(\s*)/,
    94→    bulletPoint: /^(\s*)[-*•]\s*(.*)$/,
    95→    leadingBold: /^\*\*/,
    96→  };
    97→
    98→  // Security constraints to prevent ReDoS attacks
    99→  private static readonly SECURITY_LIMITS = {
   100→    MAX_LINE_LENGTH: 1000000, // Maximum characters per line
   101→    MAX_TOTAL_LENGTH: 20000000, // Maximum total characters per document (20MB)
   102→    REGEX_TIMEOUT: 10000, // Maximum time in ms for regex operations
   103→  };
   104→
   105→  private fontState = {
   106→    currentFont: '',
   107→    currentStyle: '',
   108→    currentSize: 0,
   109→  };
   110→
   111→  constructor() {}
   112→
   113→  /**
   114→   * Add header to PDF page
   115→   */
   116→  private addHeader(doc: jsPDF, title?: string): void {
   117→    if (!title) return;
   118→
   119→    const pageWidth = doc.internal.pageSize.getWidth();
   120→    const headerY = 15;
   121→    const maxWidth = pageWidth - 30; // Leave margin on both sides
   122→
   123→    // Add header background
   124→    doc.setFillColor(248, 249, 250);
   125→    doc.rect(0, 0, pageWidth, 25, 'F');
   126→
   127→    // Add title with proper wrapping
   128→    doc.setFontSize(12);
   129→    doc.setFont('helvetica', 'bold');
   130→    doc.setTextColor(52, 73, 94);
   131→
   132→    // Truncate title if too long, or wrap it
   133→    const titleWidth = doc.getTextWidth(title);
   134→    if (titleWidth > maxWidth) {
   135→      // Truncate with ellipsis if too long
   136→      let truncatedTitle = title;
   137→      while (doc.getTextWidth(truncatedTitle + '...') > maxWidth && truncatedTitle.length > 0) {
   138→        truncatedTitle = truncatedTitle.slice(0, -1);
   139→      }
   140→      doc.text(truncatedTitle + '...', 15, headerY);
   141→    } else {
   142→      doc.text(title, 15, headerY);
   143→    }
   144→
   145→    // Add subtle line under header
   146→    doc.setDrawColor(230, 230, 230);
   147→    doc.setLineWidth(0.5);
   148→    doc.line(0, 25, pageWidth, 25);
   149→  }
   150→
   151→  /**
   152→   * Add footer to PDF page
   153→   */
   154→  private addFooter(doc: jsPDF, sourceUrl?: string, pageNumber?: number, totalPages?: number): void {
   155→    const pageWidth = doc.internal.pageSize.getWidth();
   156→    const pageHeight = doc.internal.pageSize.getHeight();
   157→    const footerY = pageHeight - 15;
   158→
   159→    // Add subtle line above footer
   160→    doc.setDrawColor(230, 230, 230);
   161→    doc.setLineWidth(0.5);
   162→    doc.line(0, pageHeight - 25, pageWidth, pageHeight - 25);
   163→
   164→    // Add footer background
   165→    doc.setFillColor(248, 249, 250);
   166→    doc.rect(0, pageHeight - 25, pageWidth, 25, 'F');
   167→
   168→    // Add source URL on left
   169→    if (sourceUrl) {
   170→      doc.setFontSize(8);
   171→      doc.setFont('helvetica', 'normal');
   172→      doc.setTextColor(100, 100, 100);
   173→      const truncatedUrl = sourceUrl.length > 60 ? sourceUrl.substring(0, 57) + '...' : sourceUrl;
   174→      doc.text(truncatedUrl, 15, footerY);
   175→    }
   176→
   177→    // Add page number on right (simplified - just page number without total for now)
   178→    if (pageNumber) {
   179→      doc.setFontSize(8);
   180→      doc.setFont('helvetica', 'normal');
   181→      doc.setTextColor(100, 100, 100);
   182→      const pageText = totalPages ? `Page ${pageNumber} of ${totalPages}` : `Page ${pageNumber}`;
   183→      const pageTextWidth = doc.getTextWidth(pageText);
   184→      doc.text(pageText, pageWidth - pageTextWidth - 15, footerY);
   185→    }
   186→  }
   187→
   188→  /**
   189→   * Update all footers with correct total page count
   190→   */
   191→  private updateAllFootersWithTotalPages(
   192→    doc: jsPDF,
   193→    pagesData: Array<{ title?: string; url?: string; markdown: string }>,
   194→  ): void {
   195→    const totalPages = doc.getNumberOfPages();
   196→
   197→    // Go through each page and update footer with correct total
   198→    for (let i = 1; i <= totalPages; i++) {
   199→      doc.setPage(i);
   200→
   201→      // Find which source page this belongs to
   202→      const sourcePageIndex = Math.min(i - 1, pagesData.length - 1);
   203→      const pageData = pagesData[sourcePageIndex];
   204→      const pageUrl = pageData?.url || '';
   205→
   206→      // Clear the existing footer area and redraw
   207→      const pageWidth = doc.internal.pageSize.getWidth();
   208→      const pageHeight = doc.internal.pageSize.getHeight();
   209→
   210→      // Redraw footer background
   211→      doc.setFillColor(248, 249, 250);
   212→      doc.rect(0, pageHeight - 25, pageWidth, 25, 'F');
   213→
   214→      // Redraw footer line
   215→      doc.setDrawColor(230, 230, 230);
   216→      doc.setLineWidth(0.5);
   217→      doc.line(0, pageHeight - 25, pageWidth, pageHeight - 25);
   218→
   219→      // Redraw footer content
   220→      const footerY = pageHeight - 15;
   221→
   222→      // Add source URL on left
   223→      if (pageUrl) {
   224→        doc.setFontSize(8);
   225→        doc.setFont('helvetica', 'normal');
   226→        doc.setTextColor(100, 100, 100);
   227→        const truncatedUrl = pageUrl.length > 60 ? pageUrl.substring(0, 57) + '...' : pageUrl;
   228→        doc.text(truncatedUrl, 15, footerY);
   229→      }
   230→
   231→      // Add page number on right with correct total
   232→      doc.setFontSize(8);
   233→      doc.setFont('helvetica', 'normal');
   234→      doc.setTextColor(100, 100, 100);
   235→      const pageText = `Page ${i} of ${totalPages}`;
   236→      const pageTextWidth = doc.getTextWidth(pageText);
   237→      doc.text(pageText, pageWidth - pageTextWidth - 15, footerY);
   238→    }
   239→  }
   240→
   241→  /**
   242→   * Add section divider
   243→   */
   244→  private addSectionDivider(doc: jsPDF, y: number): number {
   245→    const pageWidth = doc.internal.pageSize.getWidth();
   246→    const margin = PdfService.PDF_CONFIG.MARGINS.left;
   247→
   248→    doc.setDrawColor(...PdfService.PDF_CONFIG.COLORS.border);
   249→    doc.setLineWidth(0.3);
   250→    doc.line(margin, y, pageWidth - margin, y);
   251→
   252→    return y + 8; // Return new Y position after divider
   253→  }
   254→
   255→  /**
   256→   * Render text segments with proper formatting
   257→   */
   258→  private renderTextSegments(
   259→    doc: jsPDF,
   260→    segments: TextSegment[],
   261→    x: number,
   262→    y: number,
   263→    maxWidth: number,
   264→  ): { height: number; y: number } {
   265→    let currentX = x;
   266→    let currentY = y;
   267→    const lineHeight = 5; // Reduced line height for more compact layout
   268→
   269→    // Set consistent font size for text rendering
   270→    doc.setFontSize(10);
   271→    const spaceWidth = doc.getTextWidth(' ');
   272→
   273→    for (const segment of segments) {
   274→      // Set font style
   275→      if (segment.bold) {
   276→        doc.setFont('helvetica', 'bold');
   277→      } else {
   278→        doc.setFont('helvetica', 'normal');
   279→      }
   280→
   281→      // Handle links with special formatting
   282→      if (segment.isLink) {
   283→        doc.setTextColor(41, 128, 185); // Modern blue color for links
   284→        doc.setFont('helvetica', 'bold'); // Make links bold for better visibility
   285→
   286→        // Split text if it's too long
   287→        const words = segment.text.split(' ');
   288→        for (let i = 0; i < words.length; i++) {
   289→          const word = words[i];
   290→          const wordWidth = doc.getTextWidth(word);
   291→
   292→          // Check if we need to wrap to next line
   293→          if (currentX + wordWidth > x + maxWidth) {
   294→            currentY += lineHeight;
   295→            currentX = x;
   296→          }
   297→
   298→          // Add clickable link
   299→          if (segment.url) {
   300→            doc.link(currentX, currentY - 3, wordWidth, lineHeight, { url: segment.url });
   301→          }
   302→
   303→          doc.text(word, currentX, currentY);
   304→          currentX += wordWidth;
   305→
   306→          // Add space after word (except for last word)
   307→          if (i < words.length - 1) {
   308→            currentX += spaceWidth;
   309→          }
   310→        }
   311→
   312→        // Reset text color (removed URL display in parentheses)
   313→        doc.setTextColor(44, 62, 80); // Dark gray instead of pure black
   314→      } else {
   315→        // Regular text or bold text
   316→        const words = segment.text.split(' ');
   317→        for (let i = 0; i < words.length; i++) {
   318→          const word = words[i];
   319→          const wordWidth = doc.getTextWidth(word);
   320→
   321→          // Check if we need to wrap to next line
   322→          if (currentX + wordWidth > x + maxWidth) {
   323→            currentY += lineHeight;
   324→            currentX = x;
   325→          }
   326→
   327→          doc.text(word, currentX, currentY);
   328→          currentX += wordWidth;
   329→
   330→          // Add space after word (except for last word)
   331→          if (i < words.length - 1) {
   332→            currentX += spaceWidth;
   333→          }
   334→        }
   335→      }
   336→    }
   337→
   338→    return { height: currentY - y + lineHeight, y: currentY + lineHeight };
   339→  }
   340→
   341→  markdownToPdf(
   342→    markdownStrings: string[],
   343→    options?: {
   344→      title?: string;
   345→      sourceUrl?: string;
   346→      pages?: Array<{ title?: string; url?: string; markdown: string }>;
   347→    },
   348→  ): Blob {
   349→    try {
   350→      // Validate input to prevent ReDoS attacks
   351→      this.validateInputSecurity(markdownStrings, options);
   352→
   353→      const doc = this.createDocument();
   354→      const pagesData = this.preparePagesData(markdownStrings, options);
   355→
   356→      this.processAllPages(doc, pagesData);
   357→      this.finalizeDocument(doc, pagesData);
   358→
   359→      return doc.output('blob');
   360→    } catch (error) {
   361→      this.handlePdfError(error);
   362→      throw error;
   363→    }
   364→  }
   365→
   366→  private createDocument(): jsPDF {
   367→    const doc = new jsPDF();
   368→    this.resetDocumentStyle(doc);
   369→    return doc;
   370→  }
   371→
   372→  private preparePagesData(markdownStrings: string[], options?: any): PageData[] {
   373→    return options?.pages || markdownStrings.map((markdown: string) => ({ markdown }));
   374→  }
   375→
   376→  private resetDocumentStyle(doc: jsPDF): void {
   377→    doc.setTextColor(...PdfService.PDF_CONFIG.COLORS.text);
   378→    doc.setFont(PdfService.PDF_CONFIG.FONTS.body, 'normal');
   379→    this.updateFontState(PdfService.PDF_CONFIG.FONTS.body, 'normal', PdfService.PDF_CONFIG.TEXT.baseSize);
   380→  }
   381→
   382→  private updateFontState(font: string, style: string, size: number): void {
   383→    this.fontState = { currentFont: font, currentStyle: style, currentSize: size };
   384→  }
   385→
   386→  private handlePdfError(error: any): void {
   387→    this.#logger.error(`Error generating PDF from markdown: ${error.message}`, error.stack);
   388→  }
   389→
   390→  private validateInputSecurity(
   391→    markdownStrings: string[],
   392→    options?: {
   393→      title?: string;
   394→      sourceUrl?: string;
   395→      pages?: Array<{ title?: string; url?: string; markdown: string }>;
   396→    },
   397→  ): void {
   398→    // Calculate total length of all content
   399→    const totalLength =
   400→      markdownStrings.reduce((sum, str) => sum + str.length, 0) +
   401→      (options?.pages?.reduce((sum, page) => sum + page.markdown.length, 0) || 0);
   402→
   403→    if (totalLength > PdfService.SECURITY_LIMITS.MAX_TOTAL_LENGTH) {
   404→      throw new Error(
   405→        `Input too large: ${totalLength} characters exceeds maximum of ${PdfService.SECURITY_LIMITS.MAX_TOTAL_LENGTH}`,
   406→      );
   407→    }
   408→
   409→    // Check individual line lengths
   410→    const allContent = [...markdownStrings, ...(options?.pages?.map((p) => p.markdown) || [])];
   411→    for (const content of allContent) {
   412→      const lines = content.split('\n');
   413→      for (const line of lines) {
   414→        if (line.length > PdfService.SECURITY_LIMITS.MAX_LINE_LENGTH) {
   415→          throw new Error(
   416→            `Line too long: ${line.length} characters exceeds maximum of ${PdfService.SECURITY_LIMITS.MAX_LINE_LENGTH}`,
   417→          );
   418→        }
   419→      }
   420→    }
   421→  }
   422→
   423→  private finalizeDocument(doc: jsPDF, pagesData: PageData[]): void {
   424→    this.updateAllFootersWithTotalPages(doc, pagesData);
   425→  }
   426→
   427→  private processAllPages(doc: jsPDF, pagesData: PageData[]): void {
   428→    pagesData.forEach((pageData, index) => {
   429→      if (index > 0) {
   430→        doc.addPage();
   431→      }
   432→      this.processPage(doc, pageData, index);
   433→    });
   434→  }
   435→
   436→  private processPage(doc: jsPDF, pageData: PageData, pageIndex: number): void {
   437→    const context = this.createRenderContext(doc, pageData);
   438→
   439→    this.addPageHeader(context, pageData.title);
   440→    this.addPageFooter(context, pageData.url);
   441→    this.addPageBreakIndicator(context, pageIndex);
   442→
   443→    const lines = pageData.markdown.split('\n');
   444→    context.currentY = this.renderMarkdownLines(context, lines);
   445→  }
   446→
   447→  private createRenderContext(doc: jsPDF, pageData: PageData): RenderContext {
   448→    const pageWidth = doc.internal.pageSize.getWidth();
   449→    return {
   450→      doc,
   451→      currentY: PdfService.PDF_CONFIG.MARGINS.top,
   452→      pageWidth,
   453→      maxWidth: pageWidth - PdfService.PDF_CONFIG.MARGINS.left * 2,
   454→      maxY: doc.internal.pageSize.getHeight() - PdfService.PDF_CONFIG.MARGINS.bottom,
   455→      pageData,
   456→    };
   457→  }
   458→
   459→  private addPageHeader(context: RenderContext, title?: string): void {
   460→    if (!title) return;
   461→
   462→    const { doc } = context;
   463→    this.addHeader(doc, title);
   464→  }
   465→
   466→  private addPageFooter(context: RenderContext, url?: string): void {
   467→    const { doc } = context;
   468→    this.addFooter(doc, url, doc.getNumberOfPages());
   469→  }
   470→
   471→  private addPageBreakIndicator(context: RenderContext, pageIndex: number): void {
   472→    if (pageIndex === 0) return;
   473→
   474→    const { doc } = context;
   475→    const y = 30;
   476→    doc.setFontSize(8);
   477→    doc.setFont('helvetica', 'italic');
   478→    doc.setTextColor(150, 150, 150);
   479→    doc.text(`--- Source Page ${pageIndex + 1} ---`, context.pageWidth / 2, y, { align: 'center' });
   480→    doc.setTextColor(...PdfService.PDF_CONFIG.COLORS.text);
   481→  }
   482→
   483→  private renderMarkdownLines(context: RenderContext, lines: string[]): number {
   484→    let currentY = context.currentY;
   485→
   486→    for (const line of lines) {
   487→      const renderResult = this.renderMarkdownLine(context, line);
   488→      currentY = renderResult.y;
   489→
   490→      if (this.shouldCreateNewPage(currentY, context.maxY)) {
   491→        this.createNewPage(context);
   492→        currentY = PdfService.PDF_CONFIG.MARGINS.top;
   493→      }
   494→    }
   495→
   496→    return currentY;
   497→  }
   498→
   499→  private renderMarkdownLine(context: RenderContext, line: string): RenderResult {
   500→    if (this.isEmpty(line)) {
   501→      return this.renderEmptyLine(context);
   502→    }
   503→
   504→    // Use strategy pattern within the class
   505→    if (this.isHeading1(line)) return this.renderHeading1(context, line);
   506→    if (this.isHeading2(line)) return this.renderHeading2(context, line);
   507→    if (this.isHeading3(line)) return this.renderHeading3(context, line);
   508→    if (this.isBulletPoint(line)) return this.renderBulletPoint(context, line);
   509→    if (this.isNumberedList(line)) return this.renderNumberedList(context, line);
   510→    if (this.isCodeBlock(line)) return this.renderCodeBlock(context, line);
   511→
   512→    return this.renderRegularText(context, line);
   513→  }
