   665→  private renderRegularText(context: RenderContext, line: string): RenderResult {
   666→    const segments = this.parseTextSegments(line);
   667→    const result = this.renderTextSegmentsInContext(
   668→      context,
   669→      segments,
   670→      PdfService.PDF_CONFIG.MARGINS.left,
   671→      context.maxWidth,
   672→    );
