   687→
   688→  private findAllMatchesWithStaticPatterns(
   689→    line: string,
   690→  ): Array<{ start: number; end: number; segment: TextSegment }> {
   691→    // Early validation - line length should already be checked, but double-check
   692→    if (line.length > PdfService.SECURITY_LIMITS.MAX_LINE_LENGTH) {
   693→      throw new Error('Line too long for processing');
   694→    }
   695→
   696→    const allMatches: Array<{ start: number; end: number; segment: TextSegment }> = [];
   697→    const startTime = Date.now();
   698→
   699→    // Reset global regex lastIndex to ensure consistent behavior
   700→    PdfService.GLOBAL_PATTERNS.bold.lastIndex = 0;
   701→    PdfService.GLOBAL_PATTERNS.link.lastIndex = 0;
   702→    PdfService.GLOBAL_PATTERNS.trailingBold.lastIndex = 0;
   703→
   704→    // Find link matches first (higher priority)
   705→    let linkMatch;
   706→    while ((linkMatch = PdfService.GLOBAL_PATTERNS.link.exec(line)) !== null) {
   707→      // Check for timeout
   708→      if (Date.now() - startTime > PdfService.SECURITY_LIMITS.REGEX_TIMEOUT) {
   709→        throw new Error('Regex processing timeout exceeded');
   710→      }
   711→
   712→      allMatches.push({
   713→        start: linkMatch.index,
   714→        end: linkMatch.index + linkMatch[0].length,
   715→        segment: {
   716→          text: linkMatch[1],
   717→          bold: false,
   718→          isLink: true,
   719→          url: linkMatch[2],
   720→        },
   721→      });
   722→    }
   723→
   724→    // Find properly formatted bold matches
   725→    let boldMatch;
   726→    while ((boldMatch = PdfService.GLOBAL_PATTERNS.bold.exec(line)) !== null) {
   727→      // Check for timeout
   728→      if (Date.now() - startTime > PdfService.SECURITY_LIMITS.REGEX_TIMEOUT) {
   729→        throw new Error('Regex processing timeout exceeded');
   730→      }
   731→
   732→      // Check if this overlaps with any existing link matches
   733→      const overlapsWithLink = allMatches.some(
   734→        (match) =>
   735→          match.segment.isLink &&
   736→          boldMatch.index < match.end &&
   737→          boldMatch.index + boldMatch[0].length > match.start,
   738→      );
   739→
   740→      if (!overlapsWithLink) {
   741→        allMatches.push({
   742→          start: boldMatch.index,
   743→          end: boldMatch.index + boldMatch[0].length,
   744→          segment: {
   745→            text: boldMatch[2],
   746→            bold: true,
   747→            isLink: false,
   748→          },
   749→        });
   750→      }
   751→    }
   752→
   753→    // Find trailing bold patterns (e.g., "text**")
   754→    let trailingBoldMatch;
   755→    while ((trailingBoldMatch = PdfService.GLOBAL_PATTERNS.trailingBold.exec(line)) !== null) {
   756→      // Check for timeout
   757→      if (Date.now() - startTime > PdfService.SECURITY_LIMITS.REGEX_TIMEOUT) {
   758→        throw new Error('Regex processing timeout exceeded');
   759→      }
   760→
   761→      // Check if this overlaps with any existing matches
   762→      const overlapsWithExisting = allMatches.some(
   763→        (match) =>
   764→          trailingBoldMatch.index < match.end &&
   765→          trailingBoldMatch.index + trailingBoldMatch[0].length > match.start,
   766→      );
   767→
   768→      if (!overlapsWithExisting) {
   769→        allMatches.push({
   770→          start: trailingBoldMatch.index,
   771→          end: trailingBoldMatch.index + trailingBoldMatch[0].length - 2, // Don't include the **
   772→          segment: {
   773→            text: trailingBoldMatch[1],
   774→            bold: true,
   775→            isLink: false,
   776→          },
   777→        });
   778→      }
   779→    }
   780→
   781→    return allMatches.sort((a, b) => a.start - b.start);
   782→  }
   783→
   784→  private buildSegmentsFromMatches(
   785→    line: string,
   786→    matches: Array<{ start: number; end: number; segment: TextSegment }>,
   787→  ): TextSegment[] {
   788→    const segments: TextSegment[] = [];
   789→    let currentIndex = 0;
   790→
   791→    // Process the line, adding regular text between matches
   792→    for (const match of matches) {
   793→      // Add regular text before this match
   794→      if (currentIndex < match.start) {
   795→        const regularText = line.substring(currentIndex, match.start);
   796→        if (regularText) {
   797→          segments.push({
   798→            text: regularText,
   799→            bold: false,
   800→            isLink: false,
   801→          });
   802→        }
   803→      }
   804→
   805→      // Add the formatted segment
   806→      segments.push(match.segment);
   807→      currentIndex = match.end;
   808→    }
   809→
   810→    // Add any remaining regular text
   811→    if (currentIndex < line.length) {
   812→      const remainingText = line.substring(currentIndex);
   813→      if (remainingText) {
   814→        // Check if remaining text starts with bold markers that were partially processed
   815→        const cleanedText = remainingText.replace(PdfService.PARSING_PATTERNS.leadingBold, ''); // Remove leading ** if present
   816→        if (cleanedText) {
   817→          segments.push({
   818→            text: cleanedText,
   819→            bold: false,
   820→            isLink: false,
   821→          });
   822→        }
   823→      }
   824→    }
   825→
   826→    // If no formatting found, return the whole line as regular text
   827→    if (segments.length === 0 && line.trim()) {
   828→      segments.push({
   829→        text: line,
   830→        bold: false,
   831→        isLink: false,
   832→      });
   833→    }
   834→
   835→    return segments;
   836→  }
   837→
   838→  private renderTextSegmentsInContext(
   839→    context: RenderContext,
   840→    segments: TextSegment[],
   841→    x: number,
   842→    maxWidth: number,
   843→  ): RenderResult {
   844→    return this.renderTextSegments(context.doc, segments, x, context.currentY, maxWidth);
   845→  }
   846→
   847→  private setHeadingStyle(doc: jsPDF, fontSize: number): void {
   848→    this.setFont(doc, PdfService.PDF_CONFIG.FONTS.body, 'bold', fontSize);
   849→    doc.setTextColor(...PdfService.PDF_CONFIG.COLORS.header);
   850→  }
   851→
   852→  private setCodeStyle(doc: jsPDF): void {
   853→    this.setFont(doc, PdfService.PDF_CONFIG.FONTS.code, 'normal', 9);
   854→    doc.setTextColor(...PdfService.PDF_CONFIG.COLORS.code);
   855→  }
   856→
   857→  private setFont(doc: jsPDF, font: string, style: string, size: number): void {
   858→    // Only change font if different from current state
   859→    if (
   860→      this.fontState.currentFont !== font ||
   861→      this.fontState.currentStyle !== style ||
   862→      this.fontState.currentSize !== size
   863→    ) {
   864→      doc.setFont(font, style);
   865→      doc.setFontSize(size);
   866→      this.updateFontState(font, style, size);
   867→    }
   868→  }
