import { Injectable, Logger } from '@nestjs/common';
import { jsPDF } from 'jspdf';

// Interface for parsed text segments
interface TextSegment {
  text: string;
  bold: boolean;
  isLink: boolean;
  url?: string;
}

// Additional interfaces for PDF rendering
interface RenderContext {
  doc: jsPDF;
  currentY: number;
  pageWidth: number;
  maxWidth: number;
  maxY: number;
  pageData: PageData;
}

interface PageData {
  title?: string;
  url?: string;
  markdown: string;
}

interface RenderResult {
  height: number;
  y: number;
}

/**
 * Enhanced PDF Service for converting markdown content to PDF documents
 *
 * This service provides a comprehensive solution for generating PDFs from markdown content
 * with the following features:
 * - Clean, organized architecture with separated concerns
 * - Optimized font management to reduce redundant font changes
 * - Comprehensive markdown parsing (headings, links, bold text, lists, code blocks)
 * - Responsive layout with proper page breaks and spacing
 * - Configurable styling through centralized constants
 * - Support for multiple pages with headers, footers, and page indicators
 *
 * @example
 * ```typescript
 * const pdfBlob = pdfService.markdownToPdf(['# Hello World\nThis is **bold** text']);
 * ```
 */
@Injectable()
export class PdfService {
  readonly #logger = new Logger(PdfService.name);

  private static readonly PDF_CONFIG = {
    MARGINS: { top: 35, bottom: 35, left: 15, right: 15 },
    HEADER: { height: 25, fontSize: 12, yPosition: 15 },
    FOOTER: { height: 25, fontSize: 8 },
    TEXT: { baseSize: 10, lineHeight: 5, headingLineHeight: 2.5 },
    COLORS: {
      header: [52, 73, 94] as const,
      text: [44, 62, 80] as const,
      link: [41, 128, 185] as const,
      code: [85, 85, 85] as const,
      border: [230, 230, 230] as const,
      background: [248, 249, 250] as const,
    },
    FONTS: {
      body: 'helvetica',
      code: 'courier',
    },
  };

  private static readonly MARKDOWN_PATTERNS = {
    bold: /(\*\*|__)([^*_\n]*?)\1/g,
    link: /\[([^\]]+)\]\(([^)]+)\)/g,
    heading1: /^# (.+)$/,
    heading2: /^## (.+)$/,
    heading3: /^### (.+)$/,
    bullet: /^(\s*)[-*] (.+)$/,
    numbered: /^\d+\.\s(.+)$/,
    code: /^(\s{4}|\t|> )(.*)$/,
  };

  // Global regex patterns for text segment parsing (compiled once for performance)
  private static readonly GLOBAL_PATTERNS = {
    bold: /(\*\*|__)([^*_\n]*?)\1/g,
    link: /\[([^\]]+)\]\(([^)]+)\)/g,
    trailingBold: /([^*\n]+)\*\*(?!\*)/g,
  };

  // Additional regex patterns for text parsing (compiled once for performance)
  private static readonly PARSING_PATTERNS = {
    indentation: /^(\s*)/,
    bulletPoint: /^(\s*)[-*•]\s*(.*)$/,
    leadingBold: /^\*\*/,
  };

  // Security constraints to prevent ReDoS attacks
  private static readonly SECURITY_LIMITS = {
    MAX_LINE_LENGTH: 1000000, // Maximum characters per line
    MAX_TOTAL_LENGTH: 20000000, // Maximum total characters per document (20MB)
    REGEX_TIMEOUT: 10000, // Maximum time in ms for regex operations
  };

  private fontState = {
    currentFont: '',
    currentStyle: '',
    currentSize: 0,
  };

  constructor() {}

  /**
   * Add header to PDF page
   */
  private addHeader(doc: jsPDF, title?: string): void {
    if (!title) return;

    const pageWidth = doc.internal.pageSize.getWidth();
    const headerY = 15;
    const maxWidth = pageWidth - 30; // Leave margin on both sides

    // Add header background
    doc.setFillColor(248, 249, 250);
    doc.rect(0, 0, pageWidth, 25, 'F');

    // Add title with proper wrapping
    doc.setFontSize(12);
    doc.setFont('helvetica', 'bold');
    doc.setTextColor(52, 73, 94);

    // Truncate title if too long, or wrap it
    const titleWidth = doc.getTextWidth(title);
    if (titleWidth > maxWidth) {
      // Truncate with ellipsis if too long
      let truncatedTitle = title;
      while (doc.getTextWidth(truncatedTitle + '...') > maxWidth && truncatedTitle.length > 0) {
        truncatedTitle = truncatedTitle.slice(0, -1);
      }
      doc.text(truncatedTitle + '...', 15, headerY);
    } else {
      doc.text(title, 15, headerY);
    }

    // Add subtle line under header
    doc.setDrawColor(230, 230, 230);
    doc.setLineWidth(0.5);
    doc.line(0, 25, pageWidth, 25);
  }

  /**
   * Add footer to PDF page
   */
  private addFooter(doc: jsPDF, sourceUrl?: string, pageNumber?: number, totalPages?: number): void {
    const pageWidth = doc.internal.pageSize.getWidth();
    const pageHeight = doc.internal.pageSize.getHeight();
    const footerY = pageHeight - 15;

    // Add subtle line above footer
    doc.setDrawColor(230, 230, 230);
    doc.setLineWidth(0.5);
    doc.line(0, pageHeight - 25, pageWidth, pageHeight - 25);

    // Add footer background
    doc.setFillColor(248, 249, 250);
    doc.rect(0, pageHeight - 25, pageWidth, 25, 'F');

    // Add source URL on left
    if (sourceUrl) {
      doc.setFontSize(8);
      doc.setFont('helvetica', 'normal');
      doc.setTextColor(100, 100, 100);
      const truncatedUrl = sourceUrl.length > 60 ? sourceUrl.substring(0, 57) + '...' : sourceUrl;
      doc.text(truncatedUrl, 15, footerY);
    }

    // Add page number on right (simplified - just page number without total for now)
    if (pageNumber) {
      doc.setFontSize(8);
      doc.setFont('helvetica', 'normal');
      doc.setTextColor(100, 100, 100);
      const pageText = totalPages ? `Page ${pageNumber} of ${totalPages}` : `Page ${pageNumber}`;
      const pageTextWidth = doc.getTextWidth(pageText);
      doc.text(pageText, pageWidth - pageTextWidth - 15, footerY);
    }
  }

  /**
   * Update all footers with correct total page count
   */
  private updateAllFootersWithTotalPages(
    doc: jsPDF,
    pagesData: Array<{ title?: string; url?: string; markdown: string }>,
  ): void {
    const totalPages = doc.getNumberOfPages();

    // Go through each page and update footer with correct total
    for (let i = 1; i <= totalPages; i++) {
      doc.setPage(i);

      // Find which source page this belongs to
      const sourcePageIndex = Math.min(i - 1, pagesData.length - 1);
      const pageData = pagesData[sourcePageIndex];
      const pageUrl = pageData?.url || '';

      // Clear the existing footer area and redraw
      const pageWidth = doc.internal.pageSize.getWidth();
      const pageHeight = doc.internal.pageSize.getHeight();

      // Redraw footer background
      doc.setFillColor(248, 249, 250);
      doc.rect(0, pageHeight - 25, pageWidth, 25, 'F');

      // Redraw footer line
      doc.setDrawColor(230, 230, 230);
      doc.setLineWidth(0.5);
      doc.line(0, pageHeight - 25, pageWidth, pageHeight - 25);

      // Redraw footer content
      const footerY = pageHeight - 15;

      // Add source URL on left
      if (pageUrl) {
        doc.setFontSize(8);
        doc.setFont('helvetica', 'normal');
        doc.setTextColor(100, 100, 100);
        const truncatedUrl = pageUrl.length > 60 ? pageUrl.substring(0, 57) + '...' : pageUrl;
        doc.text(truncatedUrl, 15, footerY);
      }

      // Add page number on right with correct total
      doc.setFontSize(8);
      doc.setFont('helvetica', 'normal');
      doc.setTextColor(100, 100, 100);
      const pageText = `Page ${i} of ${totalPages}`;
      const pageTextWidth = doc.getTextWidth(pageText);
      doc.text(pageText, pageWidth - pageTextWidth - 15, footerY);
    }
  }

  /**
   * Add section divider
   */
  private addSectionDivider(doc: jsPDF, y: number): number {
    const pageWidth = doc.internal.pageSize.getWidth();
    const margin = PdfService.PDF_CONFIG.MARGINS.left;

    doc.setDrawColor(...PdfService.PDF_CONFIG.COLORS.border);
    doc.setLineWidth(0.3);
    doc.line(margin, y, pageWidth - margin, y);

    return y + 8; // Return new Y position after divider
  }

  /**
   * Render text segments with proper formatting
   */
  private renderTextSegments(
    doc: jsPDF,
    segments: TextSegment[],
    x: number,
    y: number,
    maxWidth: number,
  ): { height: number; y: number } {
    let currentX = x;
    let currentY = y;
    const lineHeight = 5; // Reduced line height for more compact layout

    // Set consistent font size for text rendering
    doc.setFontSize(10);
    const spaceWidth = doc.getTextWidth(' ');

    for (const segment of segments) {
      // Set font style
      if (segment.bold) {
        doc.setFont('helvetica', 'bold');
      } else {
        doc.setFont('helvetica', 'normal');
      }

      // Handle links with special formatting
      if (segment.isLink) {
        doc.setTextColor(41, 128, 185); // Modern blue color for links
        doc.setFont('helvetica', 'bold'); // Make links bold for better visibility

        // Split text if it's too long
        const words = segment.text.split(' ');
        for (let i = 0; i < words.length; i++) {
          const word = words[i];
          const wordWidth = doc.getTextWidth(word);

          // Check if we need to wrap to next line
          if (currentX + wordWidth > x + maxWidth) {
            currentY += lineHeight;
            currentX = x;
          }

          // Add clickable link
          if (segment.url) {
            doc.link(currentX, currentY - 3, wordWidth, lineHeight, { url: segment.url });
          }

          doc.text(word, currentX, currentY);
          currentX += wordWidth;

          // Add space after word (except for last word)
          if (i < words.length - 1) {
            currentX += spaceWidth;
          }
        }

        // Reset text color (removed URL display in parentheses)
        doc.setTextColor(44, 62, 80); // Dark gray instead of pure black
      } else {
        // Regular text or bold text
        const words = segment.text.split(' ');
        for (let i = 0; i < words.length; i++) {
          const word = words[i];
          const wordWidth = doc.getTextWidth(word);

          // Check if we need to wrap to next line
          if (currentX + wordWidth > x + maxWidth) {
            currentY += lineHeight;
            currentX = x;
          }

          doc.text(word, currentX, currentY);
          currentX += wordWidth;

          // Add space after word (except for last word)
          if (i < words.length - 1) {
            currentX += spaceWidth;
          }
        }
      }
    }

    return { height: currentY - y + lineHeight, y: currentY + lineHeight };
  }

  markdownToPdf(
    markdownStrings: string[],
    options?: {
      title?: string;
      sourceUrl?: string;
      pages?: Array<{ title?: string; url?: string; markdown: string }>;
    },
  ): Blob {
    try {
      // Validate input to prevent ReDoS attacks
      this.validateInputSecurity(markdownStrings, options);

      const doc = this.createDocument();
      const pagesData = this.preparePagesData(markdownStrings, options);

      this.processAllPages(doc, pagesData);
      this.finalizeDocument(doc, pagesData);

      return doc.output('blob');
    } catch (error) {
      this.handlePdfError(error);
      throw error;
    }
  }

  private createDocument(): jsPDF {
    const doc = new jsPDF();
    this.resetDocumentStyle(doc);
    return doc;
  }

  private preparePagesData(markdownStrings: string[], options?: any): PageData[] {
    return options?.pages || markdownStrings.map((markdown: string) => ({ markdown }));
  }

  private resetDocumentStyle(doc: jsPDF): void {
    doc.setTextColor(...PdfService.PDF_CONFIG.COLORS.text);
    doc.setFont(PdfService.PDF_CONFIG.FONTS.body, 'normal');
    this.updateFontState(PdfService.PDF_CONFIG.FONTS.body, 'normal', PdfService.PDF_CONFIG.TEXT.baseSize);
  }

  private updateFontState(font: string, style: string, size: number): void {
    this.fontState = { currentFont: font, currentStyle: style, currentSize: size };
  }

  private handlePdfError(error: any): void {
    this.#logger.error(`Error generating PDF from markdown: ${error.message}`, error.stack);
  }

  private validateInputSecurity(
    markdownStrings: string[],
    options?: {
      title?: string;
      sourceUrl?: string;
      pages?: Array<{ title?: string; url?: string; markdown: string }>;
    },
  ): void {
    // Calculate total length of all content
    const totalLength =
      markdownStrings.reduce((sum, str) => sum + str.length, 0) +
      (options?.pages?.reduce((sum, page) => sum + page.markdown.length, 0) || 0);

    if (totalLength > PdfService.SECURITY_LIMITS.MAX_TOTAL_LENGTH) {
      throw new Error(
        `Input too large: ${totalLength} characters exceeds maximum of ${PdfService.SECURITY_LIMITS.MAX_TOTAL_LENGTH}`,
      );
    }

    // Check individual line lengths
    const allContent = [...markdownStrings, ...(options?.pages?.map((p) => p.markdown) || [])];
    for (const content of allContent) {
      const lines = content.split('\n');
      for (const line of lines) {
        if (line.length > PdfService.SECURITY_LIMITS.MAX_LINE_LENGTH) {
          throw new Error(
            `Line too long: ${line.length} characters exceeds maximum of ${PdfService.SECURITY_LIMITS.MAX_LINE_LENGTH}`,
          );
        }
      }
    }
  }

  private finalizeDocument(doc: jsPDF, pagesData: PageData[]): void {
    this.updateAllFootersWithTotalPages(doc, pagesData);
  }

  private processAllPages(doc: jsPDF, pagesData: PageData[]): void {
    pagesData.forEach((pageData, index) => {
      if (index > 0) {
        doc.addPage();
      }
      this.processPage(doc, pageData, index);
    });
  }

  private processPage(doc: jsPDF, pageData: PageData, pageIndex: number): void {
    const context = this.createRenderContext(doc, pageData);

    this.addPageHeader(context, pageData.title);
    this.addPageFooter(context, pageData.url);
    this.addPageBreakIndicator(context, pageIndex);

    const lines = pageData.markdown.split('\n');
    context.currentY = this.renderMarkdownLines(context, lines);
  }

  private createRenderContext(doc: jsPDF, pageData: PageData): RenderContext {
    const pageWidth = doc.internal.pageSize.getWidth();
    return {
      doc,
      currentY: PdfService.PDF_CONFIG.MARGINS.top,
      pageWidth,
      maxWidth: pageWidth - PdfService.PDF_CONFIG.MARGINS.left * 2,
      maxY: doc.internal.pageSize.getHeight() - PdfService.PDF_CONFIG.MARGINS.bottom,
      pageData,
    };
  }

  private addPageHeader(context: RenderContext, title?: string): void {
    if (!title) return;

    const { doc } = context;
    this.addHeader(doc, title);
  }

  private addPageFooter(context: RenderContext, url?: string): void {
    const { doc } = context;
    this.addFooter(doc, url, doc.getNumberOfPages());
  }

  private addPageBreakIndicator(context: RenderContext, pageIndex: number): void {
    if (pageIndex === 0) return;

    const { doc } = context;
    const y = 30;
    doc.setFontSize(8);
    doc.setFont('helvetica', 'italic');
    doc.setTextColor(150, 150, 150);
    doc.text(`--- Source Page ${pageIndex + 1} ---`, context.pageWidth / 2, y, { align: 'center' });
    doc.setTextColor(...PdfService.PDF_CONFIG.COLORS.text);
  }

  private renderMarkdownLines(context: RenderContext, lines: string[]): number {
    let currentY = context.currentY;

    for (const line of lines) {
      const renderResult = this.renderMarkdownLine(context, line);
      currentY = renderResult.y;

      if (this.shouldCreateNewPage(currentY, context.maxY)) {
        this.createNewPage(context);
        currentY = PdfService.PDF_CONFIG.MARGINS.top;
      }
    }

    return currentY;
  }

  private renderMarkdownLine(context: RenderContext, line: string): RenderResult {
    if (this.isEmpty(line)) {
      return this.renderEmptyLine(context);
    }

    // Use strategy pattern within the class
    if (this.isHeading1(line)) return this.renderHeading1(context, line);
    if (this.isHeading2(line)) return this.renderHeading2(context, line);
    if (this.isHeading3(line)) return this.renderHeading3(context, line);
    if (this.isBulletPoint(line)) return this.renderBulletPoint(context, line);
    if (this.isNumberedList(line)) return this.renderNumberedList(context, line);
    if (this.isCodeBlock(line)) return this.renderCodeBlock(context, line);

    return this.renderRegularText(context, line);
  }

  private isEmpty(line: string): boolean {
    return !line.trim();
  }

  private isHeading1(line: string): boolean {
    return PdfService.MARKDOWN_PATTERNS.heading1.test(line);
  }

  private isHeading2(line: string): boolean {
    return PdfService.MARKDOWN_PATTERNS.heading2.test(line);
  }

  private isHeading3(line: string): boolean {
    return PdfService.MARKDOWN_PATTERNS.heading3.test(line);
  }

  private isBulletPoint(line: string): boolean {
    return PdfService.MARKDOWN_PATTERNS.bullet.test(line);
  }

  private isNumberedList(line: string): boolean {
    return PdfService.MARKDOWN_PATTERNS.numbered.test(line);
  }

  private isCodeBlock(line: string): boolean {
    return PdfService.MARKDOWN_PATTERNS.code.test(line);
  }

  private shouldCreateNewPage(currentY: number, maxY: number): boolean {
    return currentY > maxY;
  }

  private createNewPage(context: RenderContext): void {
    context.doc.addPage();
    this.addPageHeader(context, context.pageData.title);
    this.addPageFooter(context, context.pageData.url);
    context.currentY = PdfService.PDF_CONFIG.MARGINS.top;
  }

  private renderEmptyLine(context: RenderContext): RenderResult {
    const spacing = PdfService.PDF_CONFIG.TEXT.lineHeight * 0.6;
    context.currentY += spacing;
    return { height: spacing, y: context.currentY };
  }

  private renderHeading1(context: RenderContext, line: string): RenderResult {
    const { doc } = context;
    const text = line.substring(2);

    // Add section divider if not first heading
    if (context.currentY > PdfService.PDF_CONFIG.MARGINS.top + 5) {
      context.currentY = this.addSectionDivider(doc, context.currentY);
    }

    this.setHeadingStyle(doc, 18);
    doc.text(text, PdfService.PDF_CONFIG.MARGINS.left, context.currentY);

    const newY = context.currentY + PdfService.PDF_CONFIG.TEXT.lineHeight * 2.5;
    this.resetDocumentStyle(doc);
    context.currentY = newY;

    return { height: newY - context.currentY, y: newY };
  }

  private renderHeading2(context: RenderContext, line: string): RenderResult {
    const { doc } = context;
    const text = line.substring(3);

    // Add subtle spacing before H2
    if (context.currentY > PdfService.PDF_CONFIG.MARGINS.top + 5) {
      context.currentY += PdfService.PDF_CONFIG.TEXT.lineHeight * 0.5;
    }

    this.setHeadingStyle(doc, 14);
    doc.text(text, PdfService.PDF_CONFIG.MARGINS.left, context.currentY);

    const newY = context.currentY + PdfService.PDF_CONFIG.TEXT.lineHeight * 2;
    this.resetDocumentStyle(doc);
    context.currentY = newY;

    return { height: newY - context.currentY, y: newY };
  }

  private renderHeading3(context: RenderContext, line: string): RenderResult {
    const { doc } = context;
    const text = line.substring(4);

    this.setHeadingStyle(doc, 12);
    doc.text(text, PdfService.PDF_CONFIG.MARGINS.left, context.currentY);

    const newY = context.currentY + PdfService.PDF_CONFIG.TEXT.lineHeight * 1.5;
    this.resetDocumentStyle(doc);
    context.currentY = newY;

    return { height: newY - context.currentY, y: newY };
  }

  private renderBulletPoint(context: RenderContext, line: string): RenderResult {
    const indent = line.match(PdfService.PARSING_PATTERNS.indentation)?.[1]?.length || 0;
    const baseIndent = indent > 0 ? 15 : 10;

    // Extract the text after the bullet point marker (-, *, or •)
    const bulletMatch = line.match(PdfService.PARSING_PATTERNS.bulletPoint);
    const textContent = bulletMatch ? bulletMatch[2] : line.substring(2).trim();

    // Parse the text content for markdown formatting
    const segments = this.parseTextSegments(textContent);

    // Add the bullet point marker as a separate segment
    const bulletSegments: TextSegment[] = [{ text: '• ', bold: false, isLink: false }, ...segments];

    const result = this.renderTextSegmentsInContext(
      context,
      bulletSegments,
      PdfService.PDF_CONFIG.MARGINS.left + baseIndent,
      context.maxWidth - baseIndent,
    );

    context.currentY = result.y + 1;
    return { height: result.height + 1, y: result.y + 1 };
  }

  private renderNumberedList(context: RenderContext, line: string): RenderResult {
    const segments = this.parseTextSegments(line);
    const result = this.renderTextSegmentsInContext(
      context,
      segments,
      PdfService.PDF_CONFIG.MARGINS.left + 10,
      context.maxWidth - 10,
    );

    context.currentY = result.y + 1;
    return { height: result.height + 1, y: result.y + 1 };
  }

  private renderCodeBlock(context: RenderContext, line: string): RenderResult {
    const { doc } = context;
    this.setCodeStyle(doc);

    const text = line.startsWith('> ') ? line.substring(2) : line;
    const splitText = doc.splitTextToSize(text, context.maxWidth - 10);
    doc.text(splitText, PdfService.PDF_CONFIG.MARGINS.left + 8, context.currentY);

    const newY = context.currentY + PdfService.PDF_CONFIG.TEXT.lineHeight * splitText.length + 2;
    this.resetDocumentStyle(doc);
    context.currentY = newY;

    return { height: newY - context.currentY, y: newY };
  }

  private renderRegularText(context: RenderContext, line: string): RenderResult {
    const segments = this.parseTextSegments(line);
    const result = this.renderTextSegmentsInContext(
      context,
      segments,
      PdfService.PDF_CONFIG.MARGINS.left,
      context.maxWidth,
    );

    context.currentY = result.y + 1;
    return { height: result.height + 1, y: result.y + 1 };
  }

  private parseTextSegments(line: string): TextSegment[] {
    // Use the existing parseMarkdownLine method but with static patterns
    return this.parseMarkdownLineWithStaticPatterns(line);
  }

  private parseMarkdownLineWithStaticPatterns(line: string): TextSegment[] {
    const matches = this.findAllMatchesWithStaticPatterns(line);
    return this.buildSegmentsFromMatches(line, matches);
  }

  private findAllMatchesWithStaticPatterns(
    line: string,
  ): Array<{ start: number; end: number; segment: TextSegment }> {
    // Early validation - line length should already be checked, but double-check
    if (line.length > PdfService.SECURITY_LIMITS.MAX_LINE_LENGTH) {
      throw new Error('Line too long for processing');
    }

    const allMatches: Array<{ start: number; end: number; segment: TextSegment }> = [];
    const startTime = Date.now();

    // Reset global regex lastIndex to ensure consistent behavior
    PdfService.GLOBAL_PATTERNS.bold.lastIndex = 0;
    PdfService.GLOBAL_PATTERNS.link.lastIndex = 0;
    PdfService.GLOBAL_PATTERNS.trailingBold.lastIndex = 0;

    // Find link matches first (higher priority)
    let linkMatch;
    while ((linkMatch = PdfService.GLOBAL_PATTERNS.link.exec(line)) !== null) {
      // Check for timeout
      if (Date.now() - startTime > PdfService.SECURITY_LIMITS.REGEX_TIMEOUT) {
        throw new Error('Regex processing timeout exceeded');
      }

      allMatches.push({
        start: linkMatch.index,
        end: linkMatch.index + linkMatch[0].length,
        segment: {
          text: linkMatch[1],
          bold: false,
          isLink: true,
          url: linkMatch[2],
        },
      });
    }

    // Find properly formatted bold matches
    let boldMatch;
    while ((boldMatch = PdfService.GLOBAL_PATTERNS.bold.exec(line)) !== null) {
      // Check for timeout
      if (Date.now() - startTime > PdfService.SECURITY_LIMITS.REGEX_TIMEOUT) {
        throw new Error('Regex processing timeout exceeded');
      }

      // Check if this overlaps with any existing link matches
      const overlapsWithLink = allMatches.some(
        (match) =>
          match.segment.isLink &&
          boldMatch.index < match.end &&
          boldMatch.index + boldMatch[0].length > match.start,
      );

      if (!overlapsWithLink) {
        allMatches.push({
          start: boldMatch.index,
          end: boldMatch.index + boldMatch[0].length,
          segment: {
            text: boldMatch[2],
            bold: true,
            isLink: false,
          },
        });
      }
    }

    // Find trailing bold patterns (e.g., "text**")
    let trailingBoldMatch;
    while ((trailingBoldMatch = PdfService.GLOBAL_PATTERNS.trailingBold.exec(line)) !== null) {
      // Check for timeout
      if (Date.now() - startTime > PdfService.SECURITY_LIMITS.REGEX_TIMEOUT) {
        throw new Error('Regex processing timeout exceeded');
      }

      // Check if this overlaps with any existing matches
      const overlapsWithExisting = allMatches.some(
        (match) =>
          trailingBoldMatch.index < match.end &&
          trailingBoldMatch.index + trailingBoldMatch[0].length > match.start,
      );

      if (!overlapsWithExisting) {
        allMatches.push({
          start: trailingBoldMatch.index,
          end: trailingBoldMatch.index + trailingBoldMatch[0].length - 2, // Don't include the **
          segment: {
            text: trailingBoldMatch[1],
            bold: true,
            isLink: false,
          },
        });
      }
    }

    return allMatches.sort((a, b) => a.start - b.start);
  }

  private buildSegmentsFromMatches(
    line: string,
    matches: Array<{ start: number; end: number; segment: TextSegment }>,
  ): TextSegment[] {
    const segments: TextSegment[] = [];
    let currentIndex = 0;

    // Process the line, adding regular text between matches
    for (const match of matches) {
      // Add regular text before this match
      if (currentIndex < match.start) {
        const regularText = line.substring(currentIndex, match.start);
        if (regularText) {
          segments.push({
            text: regularText,
            bold: false,
            isLink: false,
          });
        }
      }

      // Add the formatted segment
      segments.push(match.segment);
      currentIndex = match.end;
    }

    // Add any remaining regular text
    if (currentIndex < line.length) {
      const remainingText = line.substring(currentIndex);
      if (remainingText) {
        // Check if remaining text starts with bold markers that were partially processed
        const cleanedText = remainingText.replace(PdfService.PARSING_PATTERNS.leadingBold, ''); // Remove leading ** if present
        if (cleanedText) {
          segments.push({
            text: cleanedText,
            bold: false,
            isLink: false,
          });
        }
      }
    }

    // If no formatting found, return the whole line as regular text
    if (segments.length === 0 && line.trim()) {
      segments.push({
        text: line,
        bold: false,
        isLink: false,
      });
    }

    return segments;
  }

  private renderTextSegmentsInContext(
    context: RenderContext,
    segments: TextSegment[],
    x: number,
    maxWidth: number,
  ): RenderResult {
    return this.renderTextSegments(context.doc, segments, x, context.currentY, maxWidth);
  }

  private setHeadingStyle(doc: jsPDF, fontSize: number): void {
    this.setFont(doc, PdfService.PDF_CONFIG.FONTS.body, 'bold', fontSize);
    doc.setTextColor(...PdfService.PDF_CONFIG.COLORS.header);
  }

  private setCodeStyle(doc: jsPDF): void {
    this.setFont(doc, PdfService.PDF_CONFIG.FONTS.code, 'normal', 9);
    doc.setTextColor(...PdfService.PDF_CONFIG.COLORS.code);
  }

  private setFont(doc: jsPDF, font: string, style: string, size: number): void {
    // Only change font if different from current state
    if (
      this.fontState.currentFont !== font ||
      this.fontState.currentStyle !== style ||
      this.fontState.currentSize !== size
    ) {
      doc.setFont(font, style);
      doc.setFontSize(size);
      this.updateFontState(font, style, size);
    }
  }
}

