'use client';
import { type FC, useCallback, useEffect, useRef, useState } from 'react';
import { ReviewProposalInput } from './components/review-proposals-input';
import { Button } from '@/components/ui/button';
import { Editor } from '@/components/ui/editor';
import { newBlock } from '@/lib/editor/index';
import type { JSONContent } from '@tiptap/react';
import { useParams } from 'next/navigation';
import {
  getReviewProposalById,
  updateReviewProposal,
} from '@/db/review-proposals';
import type { Message } from 'ai';
import { readStreamableValue, useActions } from 'ai/rsc';
import { v4 as uuidv4 } from 'uuid';
import type { AI } from './actions';
import type { Options } from '@/types/actions';
import { useHistory } from '../components/workspace-history-list/context';
import { cn, softPathReplace } from '@/lib/utils';
import GeneratingOutputLoader from './components/GeneratingOutputLoader';
import { SESSION_TYPES } from '@/components/editor/ExportContent';
import { ReviewProposalsSettings } from './components/review-proposals-settings';
import logger from '@/lib/logger';
import type { UserInstruction } from './types';
import axios from 'axios';
import { PsciZapIcon } from '@/components/icons/psci-icons';
import { usePostHog } from 'posthog-js/react';
import { Switch } from '@/components/ui/switch';
import { Label } from '@/shadcn/components/ui/label';
import useSWR from 'swr';
import {
  ResizableHandle,
  ResizablePanel,
  ResizablePanelGroup,
} from '@/shadcn/components/ui/resizable';
import { useWorkspaceDispatch, useWorkspaceState } from '../provider';
import { exists } from '@/lib/type-guards';

interface ReviewProposalProps {
  threadId?: string;
  history?: Message[];
  persist?: boolean;
}

/**
 * A component for reviewing proposals using AI.
 *
 * This component allows users to enter a requirement and a response, and then
 * runs an AI review on the response. The AI review generates output based on the
 * requirement and the response, which is then displayed in the component.
 *
 * The component also provides a button for running the AI review and a loading
 * indicator while the AI review is running.
 *
 * @param {string} [requirement] - The requirement to evaluate the response against.
 * @param {string} [response] - The response to evaluate.
 * @param {boolean} [persist] - Whether to persist the AI output in the database.
 *
 * @returns {ReactElement} - The ReviewProposalUI component.
 */
export const ReviewProposalUI: FC<ReviewProposalProps> = () => {
  const params = useParams();
  const dispatch = useWorkspaceDispatch();
  const workspaceState = useWorkspaceState();

  const [requirement, setRequirement] = useState('');
  const [response, setResponse] = useState('');
  const [editor, setEditor] = useState<any>(null);
  const [isGenerating, setIsGenerating] = useState(false);
  const [isRequirementsToggleOn, setIsRequirementsToggleOn] =
    useState<boolean>(false);
  const { submitUserMessage } = useActions<typeof AI>();
  const [isReviewProposalDataLoaded, setIsReviewProposalDataLoaded] =
    useState<boolean>(false);
  const [loading, setLoading] = useState(true);
  const [reviewProposal, setReviewProposal] = useState<any>(null);
  const toolbarRef = useRef<HTMLDivElement | null>(null);
  const [toolbarHeight, setToolbarHeight] = useState<number>(40);
  const { refreshData } = useHistory();
  const posthog = usePostHog();

  /**
   * Initiates the smart check process for reviewing a proposal.
   *
   * This function creates a new user message with detailed instructions
   * for evaluating a proposal response based on specific review criteria.
   * It formats the instructions and proposal details, then submits the message
   * for evaluation. The result of the evaluation is processed and displayed.
   *
   * Key steps include:
   * - Setting the generating state to true.
   * - Creating a new user message with formatted evaluation instructions and proposal details.
   * - Submitting the message for processing.
   * - Handling the response by updating the editor content and proposal state.
   * - Refreshing data and resetting the generating state upon completion.
   *
   * It also manages the editor's content and track changes in the proposal review ID.
   */
  const handleRunSmartCheck = async () => {
    setIsGenerating(true);
    posthog.capture('run_ai_review_button_clicked', {
      page: 'review-proposal',
      settings: userInstructions,
    });

    const newUserMessage: Message = {
      id: uuidv4(),
      role: 'user',
      createdAt: new Date(),
      content: `
            You are a proposal evaluator for government contracts. Your role is to critically analyze proposal responses based on specific Review Instructions provided.

            **Instructions:**

            - You will be given one or more Review Instructions, each containing a **Name** and a **Review Instruction**.
            - Apply each Review Instruction to the proposal response thoroughly.
            - For each Review Instruction, output your evaluation as per the instruction's requirements.

            **Formatting Guidelines:**

            - Begin each evaluation with the **Name** of the Review Instruction.
            - Provide your evaluation under the Name, following any specific output guidelines in the Review Instruction.
            - Separate each evaluation with two newline spacings for clarity.

            **Example Format:**

            Name: Compliance

            Evaluation:
            [Your evaluation...]

            Name: Readability Level

            Evaluation:
            [Your evaluation...]

            *Continue this pattern for all Review Instructions.*

            **Remember:**

            - Be precise, objective, and comprehensive in your evaluations.
            - Provide actionable feedback and suggestions where appropriate.
            - Ensure your language is professional and adheres to government contracting standards.

            **Here are the Review Instructions:**

            ${userInstructions?.map((ins: UserInstruction) => `Name: ${ins.proposal_reviews_ai_instruction.title}\n Review Instruction:\n${ins.proposal_reviews_ai_instruction.description}`).join('\n\n')}

            **Here is the requirement and the proposal response:**

            Requirement:
            ${requirement}

            Response:
            ${response}
        `,
    };

    const options: Partial<Options> = {
      persist: true,
      needsTitle: true,
      // @ts-expect-error not sure fix
      chatId: params.id ? params.id : null,
    };

    const result = await submitUserMessage(
      newUserMessage,
      options,
      'review-proposal',
      {
        requirement,
        response,
      }
    );

    if (result) {
      setRequirement('');
      setResponse('');

      const text = editor.getText();
      const hasExistingContent = text.trim().length > 0;
      const spaceToAdd = hasExistingContent ? '\n\n' : '';

      let tempContent = `${spaceToAdd}Requirement: ${requirement}\n\n`;
      tempContent += `Response: ${response}`;
      setEditorContent(tempContent, true);
      newBlock(editor);

      for await (const v of readStreamableValue(result.stream)) {
        if (exists(v) && 'content' in v) setEditorContent(v.content, true);

        if ('chatId' in v) {
          setReviewProposal(() => ({ ...reviewProposal, id: v.chatId }));
          softPathReplace(`/review-proposal/${v.chatId}`);
        }
      }
      await refreshData();
      setIsGenerating(false);
      // * Only do this on first run, reviewProposal is a placeholder object which only has id at this point, following is handled by onDebouncedUpdate.
      if (!reviewProposal?.user_id) saveContent(editor);
    }
  };
  const setEditorContent = useCallback(
    (content: JSONContent | string, isInsert = false) => {
      if (content) {
        const cleanContent = content.replace(/-\s*$(?![\r\n])/gm, '');
        let parsedContent = cleanContent;

        // It might be JSON content, though I can't see where it ever will be.  Catch the exception to handle
        // it not being JSON.
        try {
          parsedContent = JSON.parse(cleanContent);
          // eslint-disable-next-line @typescript-eslint/no-unused-vars
        } catch (error) {
          // Expected error so squelch.
        }

        try {
          if (isInsert) editor?.commands.insertContent(parsedContent);
          else editor?.commands.setContent(parsedContent);
        } catch (error) {
          console.error(error);
          if (isInsert)
            try {
              editor?.commands.insertContent(parsedContent);
            } catch (error) {
              console.error(error);
            }
          else editor?.commands.setContent(parsedContent);
        }
      }
    },
    [editor]
  );

  const fetchReviewProposal = useCallback(async () => {
    const reviewProposal = await getReviewProposalById(params.id as string);

    if (!reviewProposal) return;

    setReviewProposal(reviewProposal);
    editor?.commands.clearContent();
    if (exists(reviewProposal.content))
      setEditorContent(reviewProposal.content);
  }, [editor?.commands, params.id, setEditorContent]);

  useEffect(() => {
    /**
     * Fetches the review proposal data and sets the loading state to false.
     *
     * @async
     * @function
     * @returns {Promise<void>}
     */
    const fetchData = async () => {
      await fetchReviewProposal();
      setLoading(false);
      setIsReviewProposalDataLoaded(true);
    };

    if (params.id && !editor) {
      fetchData();
    } else if (editor) {
      setLoading(false);
      setIsReviewProposalDataLoaded(true);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [editor, params.id]);

  /**
   * Save the current editor content to the review proposal
   *
   * @param editor - The current editor instance
   */
  const saveContent = useCallback(
    async (editor: any) => {
      if (reviewProposal?.id && !loading && !isGenerating) {
        const updatedReviewProposal = await updateReviewProposal(
          reviewProposal.id,
          {
            content: editor.getJSON() || '',
            updated_at: new Date().toISOString(),
          }
        );
        setReviewProposal(updatedReviewProposal);
      }
    },
    [reviewProposal?.id, loading, isGenerating]
  );

  const fetcher = async (url: string) => {
    try {
      const res = await axios.get(url);
      return res?.data?.data || [];
    } catch (cause) {
      throw new Error('Failed to fetch the user instructions:', { cause });
    }
  };

  const {
    data: userInstructions,
    mutate: mutateUserInstructions,
    isLoading: isFetchingInstructions,
    error,
  } = useSWR<UserInstruction[]>('/api/ai-review/instructions', fetcher);

  if (error) logger.error('Failed to fetch the user instructions:', error);

  const isSubmitDisabled =
    !response ||
    isGenerating ||
    (isRequirementsToggleOn && !requirement) ||
    !userInstructions?.length;

  // Set the height of the toolbar dynamically to calculate the height of the editor
  const updateToolbarHeight = useCallback(() => {
    if (toolbarRef.current) setToolbarHeight(toolbarRef.current.clientHeight);
  }, []);

  useEffect(() => {
    window.addEventListener('resize', updateToolbarHeight);
    return () => {
      window.removeEventListener('resize', updateToolbarHeight);
    };
  }, [updateToolbarHeight]);

  useEffect(() => {
    updateToolbarHeight();
  }, [reviewProposal, updateToolbarHeight]);

  // Effect to handle ADD_TO_DRAFT updates
  useEffect(() => {
    if (workspaceState.contents && editor && isReviewProposalDataLoaded) {
      const text = editor.getText();
      const hasExistingContent = text.trim().length > 0;
      const spaceToAdd = hasExistingContent ? '\n\n' : '';

      setEditorContent(spaceToAdd + workspaceState.contents, true);
      saveContent(editor);
      dispatch({ type: 'SET_CONTENTS', payload: '' });
    }
  }, [
    workspaceState.contents,
    isReviewProposalDataLoaded,
    editor,
    saveContent,
    setEditorContent,
    dispatch,
  ]);

  useEffect(() => {
    if (isReviewProposalDataLoaded)
      if (reviewProposal && reviewProposal.content !== null)
        setEditorContent(reviewProposal.content);
  }, [isReviewProposalDataLoaded, reviewProposal, setEditorContent]);

  useEffect(
    () => () => {
      dispatch({ type: 'SET_CONTENTS', payload: '' });
    },
    [dispatch]
  );

  return (
    <div className='relative flex flex-col px-5 pt-3 pb-1 max-h-[calc(100vh_-_164px)]'>
      <div className='flex w-full flex-col items-center justify-between md:flex-row mb-5'>
        <div className='flex w-full flex-col'>
          <h2 className='text-2xl font-semibold'>AI Proposal Reviewer</h2>
          <p className='text-lg font-semibold text-muted-foreground py-1'>
            Instantly Review Your Content For Anything
          </p>
          <p className='text-sm font-normal text-muted-foreground'>
            Follow the steps below to use AI to analyze your content based on
            any selected criteria. The AI can check for mock evaluation scoring,
            risk, keywords, themes, color team reviews, legal compliance,
            formatting, structure, and much more.
          </p>
        </div>
      </div>
      <ResizablePanelGroup id='ai-review-panel-group' direction='horizontal'>
        <ResizablePanel
          id='input-content'
          order={1}
          minSize={25}
          className='!overflow-auto pr-3 mr-1'
        >
          <div className='flex-grow grid gap-4'>
            <div className='border border-border bg-background rounded-md p-3'>
              <div className='flex items-center mb-3'>
                <div
                  className={cn(
                    'w-8 h-8 min-w-8 min-h-8 flex border-2 rounded-full justify-center items-center mr-3 flex-none',
                    response ? 'border-success' : 'border-border'
                  )}
                >
                  1
                </div>
                <div className=''>
                  <p className='text-base font-semibold'>
                    Select Content to Review
                  </p>
                  <p className='text-sm font-normal text-muted-foreground'>
                    Start by adding your proposal content. This could be a
                    requirement response, section, executive summary, contract,
                    etc.
                  </p>
                </div>
              </div>
              <ReviewProposalInput
                placeholder='Paste or type the content you want the AI to analyze'
                setText={setResponse}
                text={response}
                testId='response-input'
              />
            </div>
            <div className='border border-border bg-background rounded-md p-3'>
              <div className='flex items-center'>
                <div
                  className={cn(
                    'w-8 h-8 min-w-8 min-h-8 flex border-2 rounded-full justify-center items-center mr-3 flex-none',
                    requirement ? 'border-success' : 'border-border'
                  )}
                >
                  2
                </div>
                <div className=''>
                  <p className='text-base font-semibold'>
                    Optional: Add RFP/RFI Requirements
                  </p>
                  <p className='text-sm font-normal text-muted-foreground'>
                    Not sure? If your proposal has a required structure, strict
                    numbered requirements, compliance rules, or evaluation
                    criteria, add them here for increased results.
                  </p>
                </div>
              </div>
              <div className='flex items-center py-3'>
                <Switch
                  id='web-search'
                  checked={isRequirementsToggleOn}
                  onCheckedChange={checked => {
                    setIsRequirementsToggleOn(checked);
                    setRequirement('');
                  }}
                  size='sm'
                  switchClassNames='size-[12px] data-[state=checked]:translate-x-4'
                  data-testid='web-search-switch'
                />
                <Label htmlFor='web-search' className='text-sm font-bold ml-2'>
                  I have RFP/RFI requirements to compare against
                </Label>
              </div>
              <ReviewProposalInput
                placeholder='Paste or type in here any requirements these sections pertain to for analysis of compliance, completeness, and a more thorough review'
                setText={setRequirement}
                text={requirement}
                disabled={!isRequirementsToggleOn}
                testId='requirement-input'
              />
            </div>
            <div className='border border-border bg-background rounded-md p-3'>
              <div className='flex items-center mb-3'>
                <div
                  className={cn(
                    'w-8 h-8 min-w-8 min-h-8 flex border-2 rounded-full justify-center items-center mr-3 flex-none',
                    userInstructions?.length
                      ? 'border-success'
                      : 'border-border'
                  )}
                >
                  3
                </div>
                <div className=''>
                  <div className='flex justify-between'>
                    <p className='text-base font-semibold'>
                      Set AI Review Instructions
                    </p>
                    {isFetchingInstructions ? (
                      <p className='text-sm font-bold text-blue-500'>
                        Loading Selections...
                      </p>
                    ) : (
                      <p
                        className={cn(
                          'text-sm font-bold',
                          userInstructions && userInstructions?.length > 0
                            ? 'text-success'
                            : 'text-destructive'
                        )}
                      >
                        {userInstructions?.length ?? 0} Instruction(s) Selected
                      </p>
                    )}
                  </div>
                  <p className='text-sm font-normal text-muted-foreground'>
                    Customize how the AI analyzes your content. Choose from
                    global presets, custom criteria, or your team&apos;s
                    library. Select multiple review areas at once for a
                    comprehensive analysis.
                  </p>
                </div>
              </div>
              <ReviewProposalsSettings
                onSelectionChange={mutateUserInstructions}
              />
            </div>
            <div className='border border-border bg-background rounded-md p-3'>
              <div className='flex items-center mb-3'>
                <div
                  className={cn(
                    'w-8 h-8 min-w-8 min-h-8 flex border-2 rounded-full justify-center items-center mr-3 flex-none',
                    isSubmitDisabled ? 'border-border' : 'border-success'
                  )}
                >
                  4
                </div>
                <div className=''>
                  <p className='text-base font-semibold'>Run AI Review</p>
                  <p className='text-sm font-normal text-muted-foreground'>
                    Click the button to start the AI review process.
                  </p>
                </div>
              </div>
              <Button
                className={cn(
                  'flex w-full',
                  isSubmitDisabled && 'opacity-50 cursor-not-allowed'
                )}
                size='sm'
                onClick={handleRunSmartCheck}
                data-testid='run-smartcheck'
                disabled={isSubmitDisabled}
              >
                {isGenerating ? (
                  'Analyzing...'
                ) : (
                  <>
                    <PsciZapIcon size={18} className='mr-2' /> Run AI Review
                  </>
                )}
              </Button>
            </div>
          </div>
        </ResizablePanel>
        <ResizableHandle withHandle className='w-1' />
        <ResizablePanel
          id='output-content'
          order={2}
          minSize={25}
          onResize={updateToolbarHeight}
        >
          <div className='p-3 border border-border bg-background rounded-md flex flex-col ml-4 h-full'>
            <h2 className='text-xl font-semibold'>AI Output</h2>
            <div className='relative w-full items-end mt-3 h-full'>
              {isGenerating && <GeneratingOutputLoader />}
              <div
                className='flex-grow h-full'
                style={{ opacity: isGenerating ? 0.5 : 1 }}
              >
                <Editor
                  showToolbar={true}
                  onCreate={setEditor}
                  onDebouncedUpdate={saveContent}
                  disableLocalStorage={true}
                  defaultValue={
                    reviewProposal?.content
                      ? JSON.parse(reviewProposal.content)
                      : ''
                  }
                  contentClassName='overflow-y-auto rounded-md'
                  className='sm:mb-0 !min-h-[calc(100vh_-_390px)] h-[calc(100vh_-_390px)]'
                  sessionType={SESSION_TYPES.REVIEW_PROPOSAL}
                  fileName={reviewProposal?.name || ''}
                  toolbarRef={toolbarRef as React.RefObject<HTMLDivElement>}
                  contentStyles={{ height: `calc(97% - ${toolbarHeight}px)` }}
                />
              </div>
            </div>
          </div>
        </ResizablePanel>
      </ResizablePanelGroup>
    </div>
  );
};

