'use client';

import { Input } from '@/components/ui/input';
import { cn, toKebabCase } from '@/lib/utils';
import Link from 'next/link';
import { usePathname } from 'next/navigation';
import {
  type MouseEventHandler,
  type MouseEvent,
  forwardRef,
  useCallback,
  useState,
  useRef,
  useEffect,
} from 'react';
import debounce from 'lodash/debounce';

import CommonDialogBox from '@/components/ui/CommonDialogBox';
import { capitalizeAndJoin } from '@/components/utility/capitalize-and-join';
import { AttachToOpportunityDialog } from './attach-to-opportunity-dialog';
import type { ItemGroup } from '../types';
import type {
  HistoryRangeDateState,
  SessionType,
} from '../ui/history-filter/typings';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSub,
  DropdownMenuSubContent,
  DropdownMenuSubTrigger,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import {
  PsciEllipsisVertical,
  PsciPaperclipIcon,
  PsciEditIcon,
  PsciTrashIcon,
  PsciUploadIcon,
  PsciMessageCircle,
  PsciClipboardCheck,
  PsciFileText,
  PsciTag,
  PsciInfoIcon,
  PsciFolderPlusIcon,
} from '@/components/icons/psci-icons';
import {
  exportContent,
  FILE_TYPES,
  SESSION_TYPES,
} from '@/components/editor/ExportContent';
import { usePostHog } from 'posthog-js/react';
import { SessionTagEditorDialog } from './session-tag-editor-dialog';
import { Badge } from '@/components/ui/badge';
import { useHistory } from './context';
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from '@/components/ui/tooltip';

export type SessionItemTag = {
  session_tag_id: string;
  name: string;
  color: string;
};
import { useHistoryPanel } from '@/context/historyPanelContext/history-panel-context';
import { MoveSessionsToFolderDialog } from './move-sessions-to-folder-dialog';

export type HistoryItemType = {
  id: string;
  type: SessionType;
  created_at: string;
  updated_at: string | null;
  folder_id?: string | null;
  item_group_id?: string | null;
  name: string | null;
  tags: SessionItemTag[] | null;
};

export type FilterPayload = {
  query: string;
  sessionType?: SessionType;
  rangeDate?: HistoryRangeDateState;
  selectedSessionTags?: SessionItemTag[];
};

export type HistoryContextType = {
  itemsWithItemGroup: HistoryItemType[] | undefined;
  sortedData: Record<string, HistoryItemType[]>;
  itemGroups: ItemGroup[] | undefined;
  deleteItem: (type: string, id: string, isActive: boolean) => Promise<void>;
  deleteItems: (
    historyItems: HistoryItemType[],
    activeItemId: string
  ) => Promise<string[]>;
  moveSessionsToItemGroup: (
    sessions: HistoryItemType[],
    groupItemId: string
  ) => Promise<string[]>;
  updateItem: (type: string, id: string, name: string) => Promise<void>;
  setFilters: React.Dispatch<React.SetStateAction<FilterPayload>>;
  filters: FilterPayload;
  isLoading: boolean;
  isInitialLoading: boolean;
  refreshData: () => Promise<void>;
  createItemGroup: (name?: string) => Promise<void>;
  updateItemGroup: (
    itemId: string,
    groupItemId: string | null
  ) => Promise<void>;
  updateItemGroupName: (groupItem: ItemGroup, name: string) => Promise<void>;
  deleteItemGroup: (id: string) => Promise<void>;
  selectedItem: HistoryItemType | null;
  setSelectedItem: (item: HistoryItemType | null) => void;
};

interface HistoryItemProps {
  type: SessionType;
  item: HistoryItemType;
  onDelete?: (type: string, id: string, isActive: boolean) => void;
  onUpdate?: (type: string, id: string, name: string) => void;
  refetchTags: () => Promise<void>;
}

export const chatTypeToIcon = (
  type: SessionType,
  isActiveItem: boolean,
  isHistoryPanelCollapsed = false,
  size = 16
) => {
  const iconStyle = cn(
    'inline',
    isActiveItem && isHistoryPanelCollapsed && 'text-primary'
  );
  switch (type) {
    case 'chat':
      return (
        <PsciMessageCircle
          size={size}
          className={cn(iconStyle, 'text-green-500')}
        />
      );
    case 'reviewProposal':
      return (
        <PsciClipboardCheck
          size={size}
          className={cn(iconStyle, 'text-purple-500')}
        />
      );
    case 'draft':
      return (
        <PsciFileText size={size} className={cn(iconStyle, 'text-pink-500')} />
      );
    default:
      return null;
  }
};
export const HistoryItem = forwardRef<HTMLAnchorElement, HistoryItemProps>(
  ({ type, item, onDelete, onUpdate, refetchTags }, ref) => {
    const { refreshData } = useHistory();
    const [openEditDialog, setOpenEditDialog] = useState(false);
    const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
    const [itemName, setItemName] = useState(item.name ?? '');
    const [
      isOpenAttachToOpportunityDialog,
      setIsOpenAttachToOpportunityDialog,
    ] = useState<boolean>(false);
    const [isOpenSessionTagEditorDialog, setIsOpenSessionTagEditorDialog] =
      useState<boolean>(false);
    const [isOpenMoveToFolderDialog, setIsOpenMoveToFolderDialog] =
      useState(false);
    const pathname = usePathname();
    const pathSegments = pathname.split('/');
    const itemId = pathSegments[2];
    const posthog = usePostHog();
    const { setSelectedItem } = useHistory();
    const tagsDivRef = useRef<HTMLDivElement>(null);
    const [maxVisibleTags, setMaxVisibleTags] = useState<number>(0);
    const [calculatingVisibleTags, setCalculatingVisibleTags] = useState(true);
    const showTagsToolTipBadge =
      !calculatingVisibleTags && item.tags && item.tags.length > maxVisibleTags;

    const isActiveItem = item.id === itemId;
    const link =
      type === 'reviewProposal'
        ? `/review-proposal/${item.id}`
        : `/${type}/${item.id}`;

    const onInputNameChange = useCallback(
      (e: React.ChangeEvent<HTMLInputElement>) => {
        const value = e.target.value.trimStart();
        setItemName(value);
      },
      []
    );

    const handleDeleteConfirmation = useCallback(() => {
      onDelete?.(type, item.id, isActiveItem);
      setOpenDeleteDialog(false);
    }, [type, item.id, isActiveItem, onDelete]);

    const handleUpdateConfirmation = useCallback(() => {
      onUpdate?.(type, item.id, itemName);
      setOpenEditDialog(false);
    }, [type, item.id, itemName, onUpdate]);

    const handleOnEditIconClick: MouseEventHandler<HTMLDivElement> =
      useCallback(e => {
        e.stopPropagation();
        setOpenEditDialog(true);
      }, []);

    const handleEditCancel: MouseEventHandler<HTMLDivElement> =
      useCallback(() => {
        setOpenEditDialog(false);
        setItemName(item.name ?? '');
      }, [item.name]);

    const handleOnDeleteIconClick: MouseEventHandler<HTMLDivElement> =
      useCallback(e => {
        e.stopPropagation();
        setOpenDeleteDialog(true);
      }, []);

    const handleAttachToOpportunityClick: MouseEventHandler<HTMLDivElement> =
      useCallback(
        e => {
          e.stopPropagation();
          setIsOpenAttachToOpportunityDialog(true);
          posthog.capture('attach_to_opportunity_button_clicked');
        },
        [posthog]
      );

    const handleMoveToProjectClick: MouseEventHandler<HTMLDivElement> =
      useCallback(
        e => {
          e.stopPropagation();
          setIsOpenMoveToFolderDialog(true);
        },
        [setIsOpenMoveToFolderDialog]
      );

    const handleSessionEditTagsClick: MouseEventHandler<HTMLDivElement> =
      useCallback(
        e => {
          e.stopPropagation();
          setIsOpenSessionTagEditorDialog(true);
          posthog.capture('session_edit_tags_button_clicked');
        },
        [posthog]
      );

    const handleExport = useCallback(
      async (
        e: MouseEvent<HTMLDivElement>,
        type: FILE_TYPES,
        sessionType: SESSION_TYPES
      ) => {
        e.stopPropagation();
        await exportContent(type, item.id, item.name ?? '', sessionType);
      },
      [item.id, item.name]
    );
    const { isHistoryPanelCollapsed, toggleHistoryPanel } = useHistoryPanel();

    const visibleTags = item.tags
      ?.slice(0, maxVisibleTags)
      .map((tag: SessionItemTag) => (
        <Badge
          className='max-w-[160px] truncate inline-block text-foreground'
          key={tag.name}
          style={{ backgroundColor: tag.color }}
        >
          {tag.name}
        </Badge>
      ));

    const tooltipBadgeTags = item.tags?.map((tag: SessionItemTag) => (
      <Badge key={tag.name} style={{ backgroundColor: tag.color }}>
        {tag.name}
      </Badge>
    ));

    useEffect(() => {
      if (!tagsDivRef.current || isHistoryPanelCollapsed) return;

      const calculateVisibleTags = (entry: ResizeObserverEntry) => {
        setCalculatingVisibleTags(true);
        const availableTagsDivWidth = entry.contentRect.width;
        const maxTagWidth = 160;
        let tagsThatCanBeShown = 0;
        let tagsDivWidth = 0;

        if (item.tags)
          for (const tag of item.tags) {
            // Assuming each character is 10px wide
            // If a tag has only one character, shadcn badge uses approx 30px to display it
            let tagWidth = 20 + tag.name.length * 10;

            if (tagWidth > maxTagWidth) tagWidth = 160;

            // 4px is gap between tags
            tagWidth += 4;

            // 40px for tooltip badge
            if (tagsDivWidth + tagWidth + 40 < availableTagsDivWidth) {
              tagsDivWidth += tagWidth;
              tagsThatCanBeShown += 1;
            } else {
              break;
            }
          }

        setMaxVisibleTags(tagsThatCanBeShown);
        setCalculatingVisibleTags(false);
      };

      const debouncedCalculateVisibleTags = debounce(calculateVisibleTags, 200);

      const observer = new ResizeObserver(entries => {
        // Since we are only observing one element, we can safely use entries[0]
        debouncedCalculateVisibleTags(entries[0]);
      });

      observer.observe(tagsDivRef.current);

      return () => {
        debouncedCalculateVisibleTags.cancel();
        observer.disconnect();
      };
    }, [isHistoryPanelCollapsed, item.tags]);

    return (
      <>
        {!isHistoryPanelCollapsed && (
          <Link
            ref={ref}
            href={link}
            className={cn(
              'text-sm justify-between text-muted-foreground overflow-hidden truncate whitespace-nowrap rounded group my-1 px-2 py-1 mx-2 flex flex-wrap items-center hover:bg-muted hover:text-foreground cursor-pointer transition-all',
              isActiveItem && 'bg-muted'
            )}
            onClick={() => setSelectedItem(item)}
          >
            <div className={cn('truncate max-w-[90%] flex')}>
              <div className='mr-1.5 -mt-[2px]'>
                {chatTypeToIcon(type, isActiveItem, isHistoryPanelCollapsed)}
              </div>
              <span title={item.name ?? ''} className='truncate'>
                {(item.name ?? '').slice(0, 40)}
              </span>
            </div>
            <DropdownMenu>
              <DropdownMenuTrigger
                className='-mr-1'
                onClick={e => e.stopPropagation()}
              >
                <PsciEllipsisVertical
                  size={16}
                  data-testid={`more-options-${toKebabCase(item?.name ?? '')}`}
                />
              </DropdownMenuTrigger>
              <DropdownMenuContent>
                <DropdownMenuItem
