                <DropdownMenuItem
                  className='flex gap-2 text-xs py-2'
                  onClick={handleAttachToOpportunityClick}
                  data-testid={`attach-to-opportunity-${toKebabCase(item?.name ?? '')}`}
                >
                  <PsciPaperclipIcon className='size-4' />
                  Attach to Opportunity
                </DropdownMenuItem>
                <DropdownMenuItem
                  className='flex gap-2 text-xs py-2'
                  onClick={handleMoveToProjectClick}
                  data-testid={`move-to-project-${toKebabCase(item?.name ?? '')}`}
                >
                  <PsciFolderPlusIcon className='size-4' />
                  Move to Project
                </DropdownMenuItem>
                {(type === SESSION_TYPES.REVIEW_PROPOSAL ||
                  type === SESSION_TYPES.DRAFT) && (
                  <DropdownMenuSub>
                    <DropdownMenuSubTrigger
                      data-testid={`export-button-${toKebabCase(item?.name ?? '')}`}
                      className='flex gap-2 text-xs py-2'
                    >
                      <PsciUploadIcon className='size-4' /> Export
                    </DropdownMenuSubTrigger>
                    <DropdownMenuSubContent>
                      <DropdownMenuItem
                        className='flex gap-2 text-xs py-2'
                        onClick={e =>
                          handleExport(e, FILE_TYPES.PDF, type as SESSION_TYPES)
                        }
                        data-testid={`export-to-pdf-${toKebabCase(item?.name ?? '')}`}
                      >
                        <PsciUploadIcon className='size-4' /> PDF
                      </DropdownMenuItem>
                      <DropdownMenuItem
                        className='flex gap-2 text-xs py-2'
                        onClick={e =>
                          handleExport(
                            e,
                            FILE_TYPES.DOCX,
                            type as SESSION_TYPES
                          )
                        }
                        data-testid={`export-to-docx-${toKebabCase(item?.name ?? '')}`}
                      >
                        <PsciUploadIcon className='size-4' /> DOCX
                      </DropdownMenuItem>
                    </DropdownMenuSubContent>
                  </DropdownMenuSub>
                )}
                <DropdownMenuItem
                  className='flex gap-2 text-xs py-2'
                  onClick={handleOnDeleteIconClick}
                  data-testid={`delete-${toKebabCase(item?.name ?? '')}`}
                >
                  <PsciTrashIcon className='size-4' /> Delete
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
            {item?.tags && item.tags.length > 0 && (
              <div
                className={cn(
                  'flex items-center gap-1 w-full flex-wrap pb-1',
                  (item.tags?.length ?? 0) > 0 && 'pt-2'
                )}
                ref={tagsDivRef}
              >
                {!calculatingVisibleTags && visibleTags}
                {showTagsToolTipBadge && (
                  <Tooltip>
                    <TooltipTrigger>
                      <Badge className='cursor-pointer'>
                        <PsciInfoIcon size={16}></PsciInfoIcon>
                      </Badge>
                    </TooltipTrigger>
                    <TooltipContent>
                      <div className='p-2 flex items-center gap-1 w-full flex-wrap'>
                        {tooltipBadgeTags}
                      </div>
                    </TooltipContent>
                  </Tooltip>
                )}
              </div>
            )}
          </Link>
        )}

        {isHistoryPanelCollapsed && (
          <div
            className='flex items-center justify-center text-center my-3'
            onClick={() => toggleHistoryPanel(false)}
          >
            {chatTypeToIcon(type, isActiveItem, isHistoryPanelCollapsed, 22)}
          </div>
        )}

        <CommonDialogBox
          open={openDeleteDialog}
          setOpen={setOpenDeleteDialog}
          title='Confirm Deletion'
          content='Are you sure you want to delete this item?'
          onContinue={handleDeleteConfirmation}
          continueButtonText='Delete'
          onCancel={() => setOpenDeleteDialog(false)}
          confirmButtonClass='bg-destructive/90 text-destructive-foreground hover:bg-destructive'
        />

        <CommonDialogBox
          open={openEditDialog}
          setOpen={setOpenEditDialog}
          title={`Update ${capitalizeAndJoin(item.type)}`}
          content={
            <Input
              value={itemName}
              onChange={onInputNameChange}
              className='max-w-[75%] mx-auto'
            />
          }
          onContinue={handleUpdateConfirmation}
          continueButtonText='Update'
          onCancel={handleEditCancel}
          confirmButtonClass='bg-primary/90 text-primary-foreground hover:bg-primary'
          disableContinueButton={!itemName}
        />

        {isOpenAttachToOpportunityDialog && (
          <AttachToOpportunityDialog
            isOpen={isOpenAttachToOpportunityDialog}
            setIsOpen={setIsOpenAttachToOpportunityDialog}
            items={[item]}
          />
        )}

        {isOpenSessionTagEditorDialog && (
          <SessionTagEditorDialog
            open={isOpenSessionTagEditorDialog}
            setIsOpen={setIsOpenSessionTagEditorDialog}
            selectedSession={item}
            refetchHistoryPanelData={refreshData}
            refetchTagsToUpdateFilter={refetchTags}
          />
        )}

        {isOpenMoveToFolderDialog && (
          <MoveSessionsToFolderDialog
            isOpen={isOpenMoveToFolderDialog}
            setIsOpen={setIsOpenMoveToFolderDialog}
            items={[item]}
          />
        )}
      </>
    );
  }
);
HistoryItem.displayName = 'HistoryItem';

