import { type NextRequest, NextResponse } from 'next/server';
import puppeteer, { type Browser } from 'puppeteer';
import { generateHTML } from '@tiptap/html';
import { TIPTAP_EXTENSIONS, fetchContent } from '@/lib/export-ai-content';
import logger from '@/lib/logger';

const PDF_RESPONSE_HEADERS = {
  'Content-Type': 'application/pdf',
  'Content-Disposition': 'attachment; filename=document.pdf',
};

const BROWSER_LAUNCH_TIMEOUT = 30000; // 30 seconds
const PAGE_LOAD_TIMEOUT = 30000; // 30 seconds
const PDF_GENERATION_TIMEOUT = 30000; // 30 seconds

/**
 * Handles GET requests to generate a PDF document from HTML content.
 * Retrieves content based on the provided `id` and `session_type` query parameter,
 * converts the content to HTML using TIPTAP extensions, and generates a PDF using Puppeteer.
 * Returns the PDF as a response with appropriate headers or an error message on failure.
 *
 * @param req - The incoming Next.js request object.
 * @param params - An object containing route parameters, including the document `id`.
 * @returns A NextResponse containing the generated PDF or an error message.
 */
export const GET = async (
  req: NextRequest,
  { params }: { params: { id: string } }
) => {
  let browser: Browser | null = null;
  try {
    logger.info('Starting PDF generation process');
    const { id } = await params;
    const searchParams: URLSearchParams = req.nextUrl.searchParams;
    const sessionType: string = searchParams.get('session_type') ?? '';

    logger.info({ id, sessionType }, 'Fetching content');
    const parsedContent = await fetchContent(id, sessionType);
    const html = generateHTML(parsedContent, TIPTAP_EXTENSIONS);

    logger.info('Launching browser for PDF generation');

    // Puppeteer known issue with alpine build: https://github.com/puppeteer/puppeteer/issues/12637#issuecomment-2264825922
    // --disable-gpu was suggested as a solution
    browser = await puppeteer.launch({
      args: ['--disable-dev-shm-usage', '--disable-gpu'],
      timeout: BROWSER_LAUNCH_TIMEOUT,
      executablePath: process.env.PUPPETEER_EXECUTABLE_PATH,
      headless: true,
    });

    logger.info('Generating PDF from content');
    const pdfBuffer = await generatePDF(browser, html);

    logger.info('PDF generation completed successfully');
    return new NextResponse(pdfBuffer, {
      status: 200,
      headers: PDF_RESPONSE_HEADERS,
    });
  } catch (error) {
    logger.error(
      {
        err: error,
        id: params?.id,
        sessionType: req.nextUrl.searchParams.get('session_type'),
      },
      'Error in PDF generation process'
    );

    return new NextResponse(
      JSON.stringify({
        error: 'Error generating PDF',
        details: error instanceof Error ? error.message : 'Unknown error',
      }),
      {
        status: 500,
        headers: { 'Content-Type': 'application/json' },
      }
    );
  } finally {
    if (browser)
      try {
        await browser.close();
        logger.info('Browser closed successfully');
      } catch (error) {
        logger.error({ err: error }, 'Error closing browser');
      }
  }
};

const generatePDF = async (browser: Browser, html: string) => {
  const page = await browser.newPage();
  try {
    logger.info('Setting page content and waiting for network idle');
    await page.setDefaultNavigationTimeout(PAGE_LOAD_TIMEOUT);
    await page.setDefaultTimeout(PAGE_LOAD_TIMEOUT);

    await page.setContent(
      `
      <html>
          <head>
            <style>
              body {
                word-wrap: break-word;
                font-family: "Segoe UI", "Segoe UI Web (West European)", -apple-system, BlinkMacSystemFont, Roboto, "Helvetica Neue", sans-serif;
              }
            </style>
          </head>
          <body>${html}</body>
      </html>
    `,
      {
        waitUntil: 'networkidle0',
        timeout: PAGE_LOAD_TIMEOUT,
      }
    );

    logger.info('Generating PDF with specified options');
    return await page.pdf({
      format: 'Letter',
      printBackground: true,
      margin: {
        top: '0.2in',
        right: '0.2in',
        bottom: '0.2in',
        left: '0.2in',
      },
      timeout: PDF_GENERATION_TIMEOUT,
    });
  } catch (error) {
    logger.error({ err: error }, 'Error in generatePDF function');
    throw error;
  } finally {
    try {
      await page.close();
      logger.info('Page closed successfully');
    } catch (error) {
      logger.error({ err: error }, 'Error closing page');
    }
  }
};

