    40→
    41→export const TIPTAP_EXTENSIONS = [
    42→  Link,
    43→  TextStyle,
    44→  Document,
    45→  Paragraph,
    46→  Text,
    47→  Bold,
    48→  Heading,
    49→  HardBreak,
