    77→
    78→/**
    79→ * Fetches the content of a session type from the database.
    80→ *
    81→ * @param id - The id of the session.
    82→ * @param sessionType - The type of session. Can be "draft" or "reviewProposal".
    83→ *
    84→ * @throws {Error} If the session content is not found or is in an invalid format.
    85→ *
    86→ * @returns The content of the session as a JSON object.
    87→ */
    88→export const fetchContent = async (id: string, sessionType: string) => {
    89→  let content;
    90→
    91→  switch (sessionType) {
    92→    case 'draft': {
    93→      content = await getDraftContentById(id);
