import Table from '@tiptap/extension-table';
import TableHeader from '@tiptap/extension-table-header';
import TableRow from '@tiptap/extension-table-row';
import TableCell from '@tiptap/extension-table-cell';
import { TextStyle } from '@tiptap/extension-text-style';
import { Link } from '@tiptap/extension-link';
import History from '@tiptap/extension-history';
import Blockquote from '@tiptap/extension-blockquote';
import HorizontalRule from '@tiptap/extension-horizontal-rule';
import Dropcursor from '@tiptap/extension-dropcursor';
import Gapcursor from '@tiptap/extension-gapcursor';
import CharacterCount from '@tiptap/extension-character-count';
import Color from '@tiptap/extension-color';
import TextAlign from '@tiptap/extension-text-align';
import Typography from '@tiptap/extension-typography';
import Placeholder from '@tiptap/extension-placeholder';

export const TIPTAP_EXTENSIONS = [
  Link,
  TextStyle,
  Document,
  Paragraph,
  Text,
  Bold,
  Heading,
  HardBreak,
  Highlight,
  Italic,
  Strike,
  BulletList,
  OrderedList,
  ListItem,
  TaskItem,
  TaskList,
  CodeBlock,
  Code,
  Underline,
  Image,
  Table,
  TableHeader,
  TableRow,
  TableCell,
  History,
  Blockquote,
  HorizontalRule,
  Dropcursor,
  Gapcursor,
  CharacterCount,
  Color,
  TextAlign,
  Typography,
  Placeholder,
];

/**
 * Fetches the content of a session type from the database.
 *
 * @param id - The id of the session.
 * @param sessionType - The type of session. Can be "draft" or "reviewProposal".
 *
 * @throws {Error} If the session content is not found or is in an invalid format.
 *
 * @returns The content of the session as a JSON object.
 */
export const fetchContent = async (id: string, sessionType: string) => {
  let content;

  switch (sessionType) {
    case 'draft': {
      content = await getDraftContentById(id);
      break;
    }
    case 'reviewProposal': {
      content = await getReviewProposalContentById(id);
      break;
    }
    default: {
      break;
    }
  }

  const type: string = sessionType
    .replace(/([A-Z])/g, ' $1')
    .replace(/^./, str => str.toUpperCase());

  if (!content) throw new Error(`${type} content not found for id: ${id}`);

  logger.info(`${type} content fetched successfully for id: ${id}`);

  try {
    return JSON.parse(content);
  } catch {
    throw new Error(`Invalid ${type} content format`);
  }
};

