import logger from '@/lib/logger';
import { createClient } from '@/lib/supabase/server';

/**
 * Fetches the content of a draft by its ID.
 *
 * @param draftId The ID of the draft to fetch.
 * @returns The content of the draft as a JSON string, or null if the draft is not found.
 * @throws An error if the request fails unexpectedly.
 */
export async function getDraftContentById(draftId: string) {
  try {
    const supabase = await createClient();
    const { data } = await supabase
      .from('drafts')
      .select('contents')
      .eq('id', draftId)
      .maybeSingle()
      .throwOnError();

    return data?.contents ?? null;
  } catch (error) {
    logger.error('Failed to fetch the draft content.', error);
    return null;
  }
}

/**
 * Fetches the content of a review proposal by its ID.
 * @param reviewProposalId - The ID of the review proposal to fetch.
 * @returns A Promise that resolves to the content of the review proposal,
 *          or null if an error occurs or the review proposal is not found.
 */
export async function getReviewProposalContentById(reviewProposalId: string) {
  try {
    const supabase = await createClient();
    const { data } = await supabase
      .from('review_proposals')
      .select('content')
      .eq('id', reviewProposalId)
      .maybeSingle()
      .throwOnError();

    return data?.content ?? null;
  } catch (error) {
    logger.error('Failed to fetch the review proposal content.', error);
    return null;
  }
}

