   439→    this.addPageHeader(context, pageData.title);
   440→    this.addPageFooter(context, pageData.url);
   441→    this.addPageBreakIndicator(context, pageIndex);
   442→
   443→    const lines = pageData.markdown.split('\n');
   444→    context.currentY = this.renderMarkdownLines(context, lines);
   445→  }
   446→
   447→  private createRenderContext(doc: jsPDF, pageData: PageData): RenderContext {
   448→    const pageWidth = doc.internal.pageSize.getWidth();
