   612→  private renderBulletPoint(context: RenderContext, line: string): RenderResult {
   613→    const indent = line.match(PdfService.PARSING_PATTERNS.indentation)?.[1]?.length || 0;
   614→    const baseIndent = indent > 0 ? 15 : 10;
   615→
   616→    // Extract the text after the bullet point marker (-, *, or •)
   617→    const bulletMatch = line.match(PdfService.PARSING_PATTERNS.bulletPoint);
   618→    const textContent = bulletMatch ? bulletMatch[2] : line.substring(2).trim();
   619→
   620→    // Parse the text content for markdown formatting
   621→    const segments = this.parseTextSegments(textContent);
   622→
   623→    // Add the bullet point marker as a separate segment
   624→    const bulletSegments: TextSegment[] = [{ text: '• ', bold: false, isLink: false }, ...segments];
   625→
   626→    const result = this.renderTextSegmentsInContext(
   627→      context,
   628→      bulletSegments,
   629→      PdfService.PDF_CONFIG.MARGINS.left + baseIndent,
   630→      context.maxWidth - baseIndent,
   631→    );
   632→
   633→    context.currentY = result.y + 1;
   634→    return { height: result.height + 1, y: result.y + 1 };
