    cn(
      'text-sm font-medium flex items-center gap-1 hover:bg-background/50',
      activeMenu === menu ? 'text-primary bg-background/50' : ''
    );

  const chevronClass = (menu: string) =>
    cn(
      'w-4 h-4 opacity-70 transition-transform',
      activeMenu === menu ? 'rotate-180' : ''
    );

  const isExportable =
    selectedItem?.type === SESSION_TYPES.DRAFT ||
    selectedItem?.type === SESSION_TYPES.REVIEW_PROPOSAL;

  const handleExport = useCallback(
    async (
      e: MouseEvent<HTMLDivElement>,
      type: FILE_TYPES,
      sessionType: SESSION_TYPES
    ) => {
      e.stopPropagation();
      await exportContent(
        type,
        selectedItem?.id ?? '',
        selectedItem?.name ?? '',
        sessionType
      );
    },
    [selectedItem?.id, selectedItem?.name]
  );

  const handleAttachToOpportunityClick: MouseEventHandler<HTMLDivElement> =
    useCallback(
      e => {
        e.stopPropagation();
        setIsOpenAttachToOpportunityDialog(true);
        posthog.capture('attach_to_opportunity_button_clicked_in_ai_header');
      },
      [posthog]
    );

  const handleMoveToFolderClick: MouseEventHandler<HTMLDivElement> =
    useCallback(
      e => {
        e.stopPropagation();
        setIsOpenMoveToFolderDialog(true);
        posthog.capture('move_to_folder_button_clicked_in_ai_header');
      },
      [posthog]
    );

  return (
    <div className='flex w-full items-center gap-2 border-b border-border bg-accent/50 px-4 py-1 shadow-sm'>
      <div className='flex items-center gap-2'>
        <DropdownMenu
          onOpenChange={open => (open ? handleOpen('sessions') : handleClose())}
        >
          <DropdownMenuTrigger asChild>
            <Button variant='ghost' className={buttonClass('sessions')}>
              Sessions
              <PsciChevronDown className={chevronClass('sessions')} />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent>
            <NewSubMenu />
            <DropdownMenuSeparator />
            <SaveAsSubMenu />
            <OpenSubMenu />
          </DropdownMenuContent>
        </DropdownMenu>

        <DropdownMenu
          onOpenChange={open => (open ? handleOpen('view') : handleClose())}
        >
          <DropdownMenuTrigger asChild>
            <Button variant='ghost' className={buttonClass('view')}>
              View
              <PsciChevronDown className={chevronClass('view')} />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent>
            <DropdownMenuItem
              className='cursor-pointer'
              onClick={() => toggleHistoryPanel(!isHistoryPanelCollapsed)}
            >
              <PsciPanelRightClose className='mr-2 h-4 w-4' /> Show/Hide History
              Panel
            </DropdownMenuItem>
            <DropdownMenuItem className='cursor-pointer' disabled={true}>
              <PsciPanelRightClose className='mr-2 h-4 w-4' /> Show/Hide Source
              Panel
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>

        <DropdownMenu
          onOpenChange={open => (open ? handleOpen('share') : handleClose())}
        >
          <DropdownMenuTrigger asChild>
            <Button variant='ghost' className={buttonClass('share')}>
              Share
              <PsciChevronDown className={chevronClass('share')} />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent>
            <DropdownMenuItem
              className='cursor-pointer'
              disabled={!selectedItem || Boolean(selectedItem.item_group_id)}
              onClick={handleMoveToFolderClick}
            >
              <PsciFolderPlusIcon className='mr-2 h-4 w-4' /> Add to Folder
            </DropdownMenuItem>
            <DropdownMenuItem
              className='cursor-pointer'
              disabled={!selectedItem}
              onClick={handleAttachToOpportunityClick}
            >
              <PsciBriefcase className='mr-2 h-4 w-4' /> Add to Opportunity
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>

        <DropdownMenu
          onOpenChange={open => (open ? handleOpen('export') : handleClose())}
        >
          <DropdownMenuTrigger asChild>
            <Button variant='ghost' className={buttonClass('export')}>
              Export
              <PsciChevronDown className={chevronClass('export')} />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent>
            <DropdownMenuItem
              className='cursor-pointer'
              disabled={!isExportable}
              onClick={e =>
                handleExport(
                  e,
                  FILE_TYPES.PDF,
                  selectedItem?.type as SESSION_TYPES.DRAFT
                )
              }
            >
              <FilePdf size={38} className='mr-2 h-4 w-4' /> Export as PDF
            </DropdownMenuItem>
            <DropdownMenuItem
              className='cursor-pointer'
              disabled={!isExportable}
              onClick={e =>
                handleExport(
                  e,
                  FILE_TYPES.DOCX,
                  selectedItem?.type as SESSION_TYPES.DRAFT
                )
              }
            >
              <FileDoc size={38} className='mr-2 h-4 w-4' /> Export as Word
              Document
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>

      {isOpenAttachToOpportunityDialog && (
        <AttachToOpportunityDialog
          isOpen={isOpenAttachToOpportunityDialog}
          setIsOpen={setIsOpenAttachToOpportunityDialog}
          items={selectedItem ? [selectedItem] : []}
        />
      )}

      {isOpenMoveToFolderDialog && (
        <MoveSessionsToFolderDialog
          isOpen={isOpenMoveToFolderDialog}
          setIsOpen={setIsOpenMoveToFolderDialog}
          items={selectedItem ? [selectedItem] : []}
          pruneCheckedItems={() => {}}
        />
      )}
    </div>
  );
};

