import { Button } from '../ui/button';
import { PsciFile } from '@/components/icons/psci-icons';
import { type SESSION_TYPES, exportContent, FILE_TYPES } from './ExportContent';
import type { FC } from 'react';

interface ToolbarExportPdfProps {
  sessionType: SESSION_TYPES;
  fileName: string;
}

export const ToolbarExportPdf: FC<ToolbarExportPdfProps> = ({
  sessionType,
  fileName,
}) => {
  const exportToPdf = async () => {
    const url: string = window.location.href;
    const id: string = url.substring(url.lastIndexOf('/') + 1);
    await exportContent(FILE_TYPES.PDF, id, fileName, sessionType);
  };

  return (
    <Button
      variant='ghost'
      className='flex items-center gap-1.5 px-2 text-sm font-normal hover:bg-transparent'
      onClick={exportToPdf}
      data-testid='ai-draft-pdf-button'
    >
      <PsciFile style={{ width: '16px' }} />
      PDF
    </Button>
  );
};
