   244→   * @returns Array of sanitized records ready for Excel conversion
   245→   */
   246→  private sanitizeRecordsForExcel(
   247→    records: Record<string, any>[]
   248→  ): Record<string, any>[] {
   249→    const EXCEL_CELL_LIMIT = 32500;
   250→    return records.map((record, recordIndex) => {
   251→      const sanitizedRecord: Record<string, any> = {};
   252→      for (const key in record) {
   253→        let value = record[key];
   254→        // Handle null/undefined values first
   255→        if (value === undefined || value === null) {
   256→          sanitizedRecord[key] = '';
   257→          continue;
   258→        }
   259→        // Convert objects to JSON strings
   260→        if (typeof value === 'object') {
   261→          value = JSON.stringify(value);
   262→        }
   263→        // Convert to string if not already
   264→        if (typeof value !== 'string') {
   265→          value = String(value);
   266→        }
   267→        // Truncate if exceeds limit
   268→        if (value.length > EXCEL_CELL_LIMIT) {
   269→          this.logger.warn(
   270→            `Record ${recordIndex}: Field "${key}" exceeded limit (${value.length} chars), truncated to ${EXCEL_CELL_LIMIT} chars`
   271→          );
   272→          value = value.substring(0, EXCEL_CELL_LIMIT);
   273→        }
   274→        sanitizedRecord[key] = value;
   275→      }
   276→      return sanitizedRecord;
   277→    });
   278→  }
