   280→  /**
   281→   * Gets metadata for Salesforce objects
   282→   * @param conn - The Salesforce connection
   283→   * @param object - The Salesforce sync object containing main object and sub-objects
   284→   * @param job - The job for logging
   285→   * @returns Object containing metadata for main object and sub-objects
   286→   */
   287→  private async getSalesforceObjectsMetadata(
   288→    conn: Connection,
   289→    object: SalesforceSyncObject,
   290→    job: IntegrationSyncJob
   291→  ): Promise<{
   292→    metadataMainObject: DescribeSObjectResult;
   293→    metadataSubObjects: DescribeSObjectResult[];
   294→  }> {
   295→    // Get available object list from salesforce
   296→    const allowedObjects = await conn.describeGlobal();
   297→
   298→    // Check if main object is present in the list
   299→    const mainObjectName = allowedObjects.sobjects.find(
   300→      (s: any) => s.name === object.mainObject
   301→    );
   302→    if (!mainObjectName) {
   303→      throw new Error(
   304→        `Main object ${object.mainObject} not found in allowed objects.`
   305→      );
   306→    }
   307→    job.log(`Main object allowed: ${object.mainObject}`);
   308→
   309→    // Check if subobjects are present in the list, if not remove them from the list
   310→    const objectToInclude: string[] = object.subObjects.filter(objectName => {
   311→      const foundObject = allowedObjects.sobjects.find(
   312→        (s: any) => s.name === objectName
   313→      );
   314→      if (!foundObject) {
   315→        this.logger.error(
   316→          `SubObject ${objectName} not found in allowed objects.`
   317→        );
   318→        return false;
   319→      }
   320→      return true;
   321→    });
   322→    job.log(`Sub objects allowed: ${objectToInclude.join(', ')}`);
   323→
   324→    // Get metadata of main object
   325→    const metadataMainObject = await conn.sobject(object.mainObject).describe();
   326→
   327→    // Get metadata of sub-objects
   328→    const metadataSubObjectsPromises = await Promise.allSettled(
   329→      objectToInclude.map(objectName => conn.sobject(objectName).describe())
   330→    );
   331→
   332→    // Get value from the promise
   333→    const metadataSubObjects: DescribeSObjectResult[] =
   334→      metadataSubObjectsPromises
   335→        .filter(
   336→          (result): result is PromiseFulfilledResult<DescribeSObjectResult> =>
   337→            result.status === 'fulfilled'
   338→        )
   339→        .map(result => result.value);
   340→
   341→    return { metadataMainObject, metadataSubObjects };
   342→  }
   343→
   344→  /**
   345→   * Loads a secret from the secrets service. Uses the worker's SecretsService instead of direct caching.
   346→   * @param apiTokenKey - The name of the secret to load.
   347→   * @returns The secret data.
   348→   * @throws Error if the secret cannot be loaded
   349→   */
   350→  private async getCachedSecret(apiTokenKey: string): Promise<any> {
   351→    const config = getConfig();
   352→
   353→    this.logger.debug('Loading secret', {
   354→      tenant: config.TENANT,
   355→      apiTokenKey,
   356→    });
   357→
   358→    try {
   359→      const secret = await this.secretsService.getSecret(
   360→        apiTokenKey,
   361→        config.TENANT
   362→      );
   363→      return secret;
   364→    } catch (error) {
   365→      const errorMessage =
   366→        error instanceof Error ? error.message : 'Unknown error';
   367→      this.logger.error('Failed to load secret', {
   368→        tenant: config.TENANT,
   369→        apiTokenKey,
   370→        error: errorMessage,
   371→      });
   372→      throw new Error(`Failed to load secret ${apiTokenKey}: ${errorMessage}`);
   373→    }
   374→  }
