   376→  /**
   377→   * Constructs a SOQL query string based on the provided Salesforce object metadata.
   378→   * Only selects fields that are present in SALESFORCE_OBJECT_SELECTED_FIELDS constant and available in the object.
   379→   *
   380→   * @param metadataMainObject - The metadata of the main Salesforce object, including its fields and child relationships.
   381→   * @param metadataSubObjects - An array of metadata for the related sub-objects, including their fields.
   382→   * @returns The constructed SOQL query string.
   383→   *
   384→   * @remarks
   385→   * - The query includes only fields from the main object that are in SALESFORCE_OBJECT_SELECTED_FIELDS.
   386→   * - If sub-object metadata is provided, the query includes sub-queries for each related sub-object.
   387→   * - Only includes sub-object fields that are in SALESFORCE_OBJECT_SELECTED_FIELDS.
   388→   * - The relationship name between the main object and each sub-object is determined using the `childRelationships` property of the main object metadata.
   389→   * - If a relationship name cannot be found for a sub-object, a warning is logged.
   390→   * - This is necessary to avoid the error: "Field is not valid for the object" due to permission or other issues.
   391→   */
   392→  private constructQuery(
   393→    metadataMainObject: DescribeSObjectResult,
   394→    metadataSubObjects: DescribeSObjectResult[]
   395→  ): string {
   396→    // Get allowed fields for main object from constant
   397→    const allowedMainObjectFields =
   398→      SALESFORCE_OBJECT_SELECTED_FIELDS[
   399→        metadataMainObject.name as keyof typeof SALESFORCE_OBJECT_SELECTED_FIELDS
   400→      ] || [];
   401→
   402→    // Filter main object fields to only include those in constant and available in Salesforce
   403→    const availableMainObjectFields = metadataMainObject.fields
   404→      .filter(
   405→        (field: any) =>
   406→          Array.isArray(allowedMainObjectFields) &&
   407→          allowedMainObjectFields.includes(field.name)
   408→      )
   409→      .map((field: any) => field.name);
   410→
   411→    if (availableMainObjectFields.length === 0) {
   412→      this.logger.warn('No allowed fields found for main object', {
   413→        mainObject: metadataMainObject.name,
   414→      });
   415→    }
   416→
   417→    const fieldsMainObject: string = availableMainObjectFields.join(', ');
   418→    let query: string = `SELECT ${fieldsMainObject}`;
   419→
   420→    if (metadataSubObjects.length > 0) {
   421→      metadataSubObjects.forEach(result => {
   422→        const subObjectApiName = result.name;
   423→
   424→        // Get allowed fields for sub-object from constant
   425→        const allowedSubObjectFields =
   426→          SALESFORCE_OBJECT_SELECTED_FIELDS[subObjectApiName] || [];
   427→
   428→        // Filter sub-object fields to only include those in constant and available in Salesforce
   429→        const availableSubObjectFields = result.fields
   430→          .filter(
   431→            (field: any) =>
   432→              Array.isArray(allowedSubObjectFields) &&
   433→              allowedSubObjectFields.includes(field.name)
   434→          )
   435→          .map((field: any) => field.name);
   436→
   437→        if (availableSubObjectFields.length === 0) {
   438→          this.logger.warn('No allowed fields found for sub-object', {
   439→            subObject: subObjectApiName,
   440→          });
   441→          return; // Skip this sub-object if no allowed fields are available
   442→        }
   443→
   444→        const fieldsSubObject: string = availableSubObjectFields.join(', ');
   445→
   446→        // Find the relationship name from the parent metadata
   447→        const relationship = metadataMainObject.childRelationships.find(
   448→          (rel: any) => rel.childSObject === subObjectApiName
   449→        );
   450→
   451→        if (relationship?.relationshipName) {
   452→          query += `, (SELECT ${fieldsSubObject} FROM ${relationship.relationshipName})`;
   453→        } else {
   454→          this.logger.warn('Relationship name not found', {
   455→            subObject: subObjectApiName,
   456→            mainObject: metadataMainObject.name,
   457→          });
   458→        }
   459→      });
   460→    }
   461→    query += ` FROM ${metadataMainObject.name}`;
   462→
   463→    this.logger.debug('Constructed SOQL query', {
   464→      query: query.substring(0, 200) + (query.length > 200 ? '...' : ''),
   465→      mainObject: metadataMainObject.name,
   466→      mainObjectFields: availableMainObjectFields,
   467→      subObjectCount: metadataSubObjects.length,
   468→    });
   469→
   470→    return query;
   471→  }
   472→}
