import type { Logger } from 'pino';
import { Connection, DescribeSObjectResult } from 'jsforce';
import * as XLSX from 'xlsx';
import { getLogger } from '../utils/logger.util.js';
import type {
  IntegrationSyncJob,
  IntegrationSyncJobResult,
  SalesforceSyncObject,
} from '../types/worker.types.js';
import type { DataSourceObjectWithRelations } from '../types/database.types.js';
import { SupabaseService } from './supabase.service.js';
import { SecretsService } from './secrets.service.js';
import { SentryService } from './sentry.service.js';
import { getConfig } from '../config/environment.js';
import { SALESFORCE_OBJECT_SELECTED_FIELDS } from '../utils/constants.js';
import { buildSalesforcePath } from '../utils/integration-path.util.js';

export class SalesforceService {
  private readonly logger: Logger;

  constructor(
    private readonly supabaseService: SupabaseService,
    private readonly secretsService: SecretsService,
    private readonly sentryService: SentryService
  ) {
    this.logger = getLogger().child({ service: 'SalesforceService' });
  }

  async handleSalesforceObjectSync(
    operation: 'CREATE' | 'UPDATE' | 'DELETE',
    job: IntegrationSyncJob,
    dataSourceObject: DataSourceObjectWithRelations
  ): Promise<IntegrationSyncJobResult> {
    const startTime = new Date();
    const config = getConfig();

    job.log(`Handling Salesforce object sync for operation: ${operation}`);

    this.logger.info('Starting Salesforce object sync', {
      operation,
      dataSourceObjectId: dataSourceObject.object_id,
      tenant: config.TENANT,
    });

    try {
      job.log('Loading Salesforce secret');
      const apiTokenKey =
        dataSourceObject.data_source_connections.api_token_key;
      if (!apiTokenKey) {
        throw new Error('No API token key found in data source connection');
      }
      const secret = await this.getCachedSecret(apiTokenKey);

      job.log('Creating Salesforce connection');
      const conn = new Connection({
        oauth2: {
          clientId: secret.clientId,
          clientSecret: secret.clientSecret,
        },
        instanceUrl: secret.instanceUrl,
        refreshToken: secret.refreshToken,
      });

      job.log('Getting raw source data');
      const salesforceSyncObject =
        dataSourceObject.raw_source as unknown as SalesforceSyncObject;

      if (!salesforceSyncObject) {
        throw new Error('No object found in raw_source');
      }

      this.logger.info('Processing Salesforce sync object', {
        mainObject: salesforceSyncObject.mainObject,
        subObjects: salesforceSyncObject.subObjects,
        name: salesforceSyncObject.name,
      });

      job.log('Loading Salesforce metadata');
      // Get metadata of Salesforce objects
      const { metadataMainObject, metadataSubObjects } =
        await this.getSalesforceObjectsMetadata(
          conn,
          salesforceSyncObject,
          job
        );

      const query: string = this.constructQuery(
        metadataMainObject,
        metadataSubObjects
      );
      let records: Record<string, any>[] = [];
      let result = await conn.query(query, { autoFetch: true, maxFetch: 2000 });
      records = records.concat(result.records);
      job.log(`Total Records: ${result.totalSize}`);

      // Handle pagination
      while (!result.done && result.nextRecordsUrl) {
        job.log(`Query more records,offset: ${records.length}`);
        result = await conn.queryMore(result.nextRecordsUrl, {
          autoFetch: true,
          maxFetch: 2000,
          headers: {},
          scanAll: false,
          responseTarget: 'QueryResult',
        });
        records = records.concat(result.records);
      }

      job.log('Converting records to Excel format');
      // Convert records to Excel
      const workbook = XLSX.utils.book_new();
      const sanitizedRecords = this.sanitizeRecordsForExcel(records);
      const worksheet = XLSX.utils.json_to_sheet(sanitizedRecords);
      XLSX.utils.book_append_sheet(workbook, worksheet, 'Data');
      const excelBuffer = XLSX.write(workbook, {
        type: 'buffer',
        bookType: 'xlsx',
      });

      // Load tenant information for divisions support
      const { data: tenant, error: loadTenantErr } =
        await this.supabaseService.loadTenant();
      if (loadTenantErr) {
        job.log(
          'Failed to load tenant information, continuing without divisions'
        );
      }

      // Load division information if divisions are enabled
      let divisionId = null;
      if (tenant && tenant?.enable_divisions) {
        const dataSourceConnectionId =
          dataSourceObject.data_source_connection_id;
        if (dataSourceConnectionId) {
          const { data: division, error: loadDivisionErr } =
            await this.supabaseService.loadDivisionFromDataSourceConnection(
              dataSourceConnectionId
            );
          if (loadDivisionErr) {
            job.log(
              `Failed to load division information: ${loadDivisionErr.message}`
            );
          } else {
            divisionId = division?.division_id;
          }
        }
      }

      // Generate upload path using the new utility
      const uploadPath = buildSalesforcePath({
        userId: dataSourceObject.data_source_connections.user_id,
        integrationName: dataSourceObject.data_source_connections.name,
        mainObject: salesforceSyncObject.mainObject,
        syncObjectName: salesforceSyncObject.name,
        isDivisionsEnabled: tenant?.enable_divisions,
        divisionId: divisionId,
        isShared: dataSourceObject.data_source_connections.shared ?? false,
      });

      job.log(`Uploading Excel to Supabase at path: ${uploadPath}`);

      const { error: uploadObjectErr } =
        await this.supabaseService.upsertStorageObject(
          uploadPath,
          excelBuffer,
          {
            contentType: 'application/vnd.ms-excel',
          }
        );

      if (uploadObjectErr) {
        this.logger.error('Upload object error', {
          error: uploadObjectErr.message,
          uploadPath,
        });
        throw new Error(
          `Failed to upload File to Supabase: ${uploadObjectErr.message}`
        );
      }

      job.log('Salesforce sync completed successfully');

      const endTime = new Date();
      const durationMs = endTime.getTime() - startTime.getTime();

      this.logger.info('Salesforce object sync completed', {
        operation,
        dataSourceObjectId: dataSourceObject.object_id,
        recordCount: records.length,
        durationMs,
        uploadPath,
      });

      return {
        success: true,
        processedAt: endTime.toISOString(),
        durationMs,
        statusCode: 200,
        body: {
          type: 'salesforce_sync',
          operation,
          recordCount: records.length,
          uploadPath,
          mainObject: salesforceSyncObject.mainObject,
          subObjects: salesforceSyncObject.subObjects,
        },
      };
    } catch (error) {
      const endTime = new Date();
      const durationMs = endTime.getTime() - startTime.getTime();
      const errorMessage =
        error instanceof Error ? error.message : 'Unknown error';

      this.logger.error('Salesforce object sync failed', {
        error: errorMessage,
        operation,
        dataSourceObjectId: dataSourceObject.object_id,
        durationMs,
      });

      // Capture exception in Sentry
      this.sentryService.captureException(error as Error);

      return {
        success: false,
        processedAt: endTime.toISOString(),
        durationMs,
        statusCode: 500,
        error: `SalesforceObjectSyncError: ${errorMessage}`,
        body: {
          type: 'salesforce_sync_error',
          operation,
          error: errorMessage,
        },
      };
    }
  }

  /**
   * Sanitizes Salesforce records for Excel export
   * Handles conversion of object fields to strings and null/undefined values
   * Truncates string values that exceed Excel's 32,500 character cell limit
   * @param records - The records to sanitize
   * @returns Array of sanitized records ready for Excel conversion
   */
  private sanitizeRecordsForExcel(
    records: Record<string, any>[]
  ): Record<string, any>[] {
    const EXCEL_CELL_LIMIT = 32500;
    return records.map((record, recordIndex) => {
      const sanitizedRecord: Record<string, any> = {};
      for (const key in record) {
        let value = record[key];
        // Handle null/undefined values first
        if (value === undefined || value === null) {
          sanitizedRecord[key] = '';
          continue;
        }
        // Convert objects to JSON strings
        if (typeof value === 'object') {
          value = JSON.stringify(value);
        }
        // Convert to string if not already
        if (typeof value !== 'string') {
          value = String(value);
        }
        // Truncate if exceeds limit
        if (value.length > EXCEL_CELL_LIMIT) {
          this.logger.warn(
            `Record ${recordIndex}: Field "${key}" exceeded limit (${value.length} chars), truncated to ${EXCEL_CELL_LIMIT} chars`
          );
          value = value.substring(0, EXCEL_CELL_LIMIT);
        }
        sanitizedRecord[key] = value;
      }
      return sanitizedRecord;
    });
  }

  /**
   * Gets metadata for Salesforce objects
   * @param conn - The Salesforce connection
   * @param object - The Salesforce sync object containing main object and sub-objects
   * @param job - The job for logging
   * @returns Object containing metadata for main object and sub-objects
   */
  private async getSalesforceObjectsMetadata(
    conn: Connection,
    object: SalesforceSyncObject,
    job: IntegrationSyncJob
  ): Promise<{
    metadataMainObject: DescribeSObjectResult;
    metadataSubObjects: DescribeSObjectResult[];
  }> {
    // Get available object list from salesforce
    const allowedObjects = await conn.describeGlobal();

    // Check if main object is present in the list
    const mainObjectName = allowedObjects.sobjects.find(
      (s: any) => s.name === object.mainObject
    );
    if (!mainObjectName) {
      throw new Error(
        `Main object ${object.mainObject} not found in allowed objects.`
      );
    }
    job.log(`Main object allowed: ${object.mainObject}`);

    // Check if subobjects are present in the list, if not remove them from the list
    const objectToInclude: string[] = object.subObjects.filter(objectName => {
      const foundObject = allowedObjects.sobjects.find(
        (s: any) => s.name === objectName
      );
      if (!foundObject) {
        this.logger.error(
          `SubObject ${objectName} not found in allowed objects.`
        );
        return false;
      }
      return true;
    });
    job.log(`Sub objects allowed: ${objectToInclude.join(', ')}`);

    // Get metadata of main object
    const metadataMainObject = await conn.sobject(object.mainObject).describe();

    // Get metadata of sub-objects
    const metadataSubObjectsPromises = await Promise.allSettled(
      objectToInclude.map(objectName => conn.sobject(objectName).describe())
    );

    // Get value from the promise
    const metadataSubObjects: DescribeSObjectResult[] =
      metadataSubObjectsPromises
        .filter(
          (result): result is PromiseFulfilledResult<DescribeSObjectResult> =>
            result.status === 'fulfilled'
        )
        .map(result => result.value);

    return { metadataMainObject, metadataSubObjects };
  }

  /**
   * Loads a secret from the secrets service. Uses the worker's SecretsService instead of direct caching.
   * @param apiTokenKey - The name of the secret to load.
   * @returns The secret data.
   * @throws Error if the secret cannot be loaded
   */
  private async getCachedSecret(apiTokenKey: string): Promise<any> {
    const config = getConfig();

    this.logger.debug('Loading secret', {
      tenant: config.TENANT,
      apiTokenKey,
    });

    try {
      const secret = await this.secretsService.getSecret(
        apiTokenKey,
        config.TENANT
      );
      return secret;
    } catch (error) {
      const errorMessage =
        error instanceof Error ? error.message : 'Unknown error';
      this.logger.error('Failed to load secret', {
        tenant: config.TENANT,
        apiTokenKey,
        error: errorMessage,
      });
      throw new Error(`Failed to load secret ${apiTokenKey}: ${errorMessage}`);
    }
  }

  /**
   * Constructs a SOQL query string based on the provided Salesforce object metadata.
   * Only selects fields that are present in SALESFORCE_OBJECT_SELECTED_FIELDS constant and available in the object.
   *
   * @param metadataMainObject - The metadata of the main Salesforce object, including its fields and child relationships.
   * @param metadataSubObjects - An array of metadata for the related sub-objects, including their fields.
   * @returns The constructed SOQL query string.
   *
   * @remarks
   * - The query includes only fields from the main object that are in SALESFORCE_OBJECT_SELECTED_FIELDS.
   * - If sub-object metadata is provided, the query includes sub-queries for each related sub-object.
   * - Only includes sub-object fields that are in SALESFORCE_OBJECT_SELECTED_FIELDS.
   * - The relationship name between the main object and each sub-object is determined using the `childRelationships` property of the main object metadata.
   * - If a relationship name cannot be found for a sub-object, a warning is logged.
   * - This is necessary to avoid the error: "Field is not valid for the object" due to permission or other issues.
   */
  private constructQuery(
    metadataMainObject: DescribeSObjectResult,
    metadataSubObjects: DescribeSObjectResult[]
  ): string {
    // Get allowed fields for main object from constant
    const allowedMainObjectFields =
      SALESFORCE_OBJECT_SELECTED_FIELDS[
        metadataMainObject.name as keyof typeof SALESFORCE_OBJECT_SELECTED_FIELDS
      ] || [];

    // Filter main object fields to only include those in constant and available in Salesforce
    const availableMainObjectFields = metadataMainObject.fields
      .filter(
        (field: any) =>
          Array.isArray(allowedMainObjectFields) &&
          allowedMainObjectFields.includes(field.name)
      )
      .map((field: any) => field.name);

    if (availableMainObjectFields.length === 0) {
      this.logger.warn('No allowed fields found for main object', {
        mainObject: metadataMainObject.name,
      });
    }

    const fieldsMainObject: string = availableMainObjectFields.join(', ');
    let query: string = `SELECT ${fieldsMainObject}`;

    if (metadataSubObjects.length > 0) {
      metadataSubObjects.forEach(result => {
        const subObjectApiName = result.name;

        // Get allowed fields for sub-object from constant
        const allowedSubObjectFields =
          SALESFORCE_OBJECT_SELECTED_FIELDS[subObjectApiName] || [];

        // Filter sub-object fields to only include those in constant and available in Salesforce
        const availableSubObjectFields = result.fields
          .filter(
            (field: any) =>
              Array.isArray(allowedSubObjectFields) &&
              allowedSubObjectFields.includes(field.name)
          )
          .map((field: any) => field.name);

        if (availableSubObjectFields.length === 0) {
          this.logger.warn('No allowed fields found for sub-object', {
            subObject: subObjectApiName,
          });
          return; // Skip this sub-object if no allowed fields are available
        }

        const fieldsSubObject: string = availableSubObjectFields.join(', ');

        // Find the relationship name from the parent metadata
        const relationship = metadataMainObject.childRelationships.find(
          (rel: any) => rel.childSObject === subObjectApiName
        );

        if (relationship?.relationshipName) {
          query += `, (SELECT ${fieldsSubObject} FROM ${relationship.relationshipName})`;
        } else {
          this.logger.warn('Relationship name not found', {
            subObject: subObjectApiName,
            mainObject: metadataMainObject.name,
          });
        }
      });
    }
    query += ` FROM ${metadataMainObject.name}`;

    this.logger.debug('Constructed SOQL query', {
      query: query.substring(0, 200) + (query.length > 200 ? '...' : ''),
      mainObject: metadataMainObject.name,
      mainObjectFields: availableMainObjectFields,
      subObjectCount: metadataSubObjects.length,
    });

    return query;
  }
}
