} from '@/components/ui/popover'; // Prettier-ignore
import type { FileInfoView } from '@/db/file-info-view';
import { useContext, useState } from 'react';
import FilesTableDialog from './files-table-dialog';
import SelectedItems from './SelectedItems';
import { usePostHog } from 'posthog-js/react';
import { exists } from '@/lib/type-guards';
import { ChatbotUIContext } from '@/context/context';
import type { AIState } from '@/types/actions';

interface SelectedItemPopoverProps {
  selectedItems: FileInfoView[];
  onItemsChanged: (selectedItems: FileInfoView[]) => void;
  contentClassName?: string;
  showAttachButton?: boolean;
  hideFilters?: boolean;
  trigger?: React.ReactNode;
  isInline?: boolean;
  isCompactHeight?: boolean;
  isFileToggleLoading?: boolean;
  attachFilesButtonClickEventName?: string;
  filesButtonClickEventName?: string;
  onClearAIState?: ({ clearAll }: { clearAll?: boolean }) => void;
  onRemoveItemAIState?: (item: FileInfoView) => void;
  aiState?: AIState;
  isFileToggleEnabled?: boolean;
}

/**
 * A Popover component that shows a list of selected files.
 *
 * This component shows a list of files that are selected in the FilesTable.
 * It also provides a button to attach more files, and a button to clear the selection.
 *
 * The component can be used as a modal or as an inline component.
 * If `isInline` is `true`, the component will be rendered as an inline component.
 * If `isInline` is `false`, the component will be rendered as a modal.
 *
 * The component accepts the following props:
 *
 * - `selectedItems`: The list of selected files.
 * - `onItemsChanged`: A callback that will be called when the selection changes.
 * - `showAttachButton`: Whether to show the attach button.
 * - `hideFilters`: Whether to hide the filter input.
 * - `isInline`: Whether to render the component as an inline component or as a modal.
 * - `isCompactHeight`: Whether to render the component with a compact height.
 * - `contentClassName`: An optional class name to add to the content of the popover.
 */
export default function SelectedItemPopover({
  trigger,
  selectedItems = [], // Default to an empty array to prevent undefined issues
  onItemsChanged,
  showAttachButton = false,
  hideFilters = false,
  isInline = false,
  isCompactHeight = false,
  contentClassName,
  isFileToggleLoading,
  attachFilesButtonClickEventName,
  filesButtonClickEventName,
  onClearAIState,
  onRemoveItemAIState,
  isFileToggleEnabled,
}: Readonly<SelectedItemPopoverProps>) {
  const [isOpen, setIsOpen] = useState(false);
  const [filter, setFilter] = useState<string>('');
  const posthog = usePostHog();
  const [isFilesDialogOpen, setIsFilesDialogOpen] = useState(false);
  const { setOpportunityState } = useContext(ChatbotUIContext);
  /**
   * Handles the attach files button click. Opens the files dialog and
   * closes the popover.
   */
  const handleAttachFiles = () => {
    setIsFilesDialogOpen(true);
    setIsOpen(false);

    if (
      exists(attachFilesButtonClickEventName) &&
      attachFilesButtonClickEventName.length > 0
    )
      posthog.capture(attachFilesButtonClickEventName);
  };

  /**
   * Clears the selection by closing the popover and calling the callback
   * with an empty array.
   */
  const handleClearSelection = () => {
    setIsOpen(false);
    setOpportunityState(prevState => ({
      ...prevState,
      files: [],
    }));
    onClearAIState?.({ clearAll: true });
    onItemsChanged([]);
  };

  const filesPopoverChanged = (open: boolean) => {
    setFilter('');
    setIsOpen(open);

    if (
      open &&
      exists(filesButtonClickEventName) &&
      filesButtonClickEventName.length > 0
    )
      posthog.capture(filesButtonClickEventName);
  };

  return (
    <>
      {showAttachButton && (
        <FilesTableDialog
          open={isFilesDialogOpen}
          onOpenChange={setIsFilesDialogOpen}
          onSelectionChange={onItemsChanged}
          initialSelection={selectedItems}
          trigger={<div></div>}
          isFileToggleLoading={isFileToggleLoading}
        />
